{
  "name": "Kokoro",
  "description": "A quiet, private relationship companion designed to help you nurture the bonds that matter most through intentional journaling, preference tracking, and thoughtful reminders."
}

export interface Preference {
  id: string;
  category: 'like' | 'dislike' | 'fact' | 'ritual';
  content: string;
  timestamp: number;
}

export interface ImportantDate {
  id: string;
  label: string;
  date: string; // ISO format
  recurring: boolean;
}

export interface Person {
  id: string;
  name: string;
  relationship: string;
  avatar?: string;
  preferences: Preference[];
  dates: ImportantDate[];
  notes: string;
  lastInteraction?: number;
}

export interface KokoroStore {
  people: Person[];
  addPerson: (person: Omit<Person, 'id' | 'preferences' | 'dates'>) => void;
  updatePerson: (id: string, updates: Partial<Person>) => void;
  deletePerson: (id: string) => void;
  addPreference: (personId: string, pref: Omit<Preference, 'id' | 'timestamp'>) => void;
  addDate: (personId: string, date: Omit<ImportantDate, 'id'>) => void;
}

import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

const rootElement = document.getElementById('root');
if (!rootElement) {
  throw new Error("Could not find root element to mount to");
}

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

import { GoogleGenAI } from "@google/genai";
import { Person } from "../types";

const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

export const getRelationshipAdvice = async (person: Person) => {
  const preferencesText = person.preferences.map(p => `- ${p.category}: ${p.content}`).join('\n');
  const datesText = person.dates.map(d => `- ${d.label}: ${d.date}`).join('\n');

  const prompt = `
    Based ONLY on the following information about ${person.name}, who is my ${person.relationship}, provide 3 thoughtful, small gestures or gift ideas.

    Preferences:
    ${preferencesText}

    Important Dates:
    ${datesText}

    Context:
    ${person.notes}

    Be minimalist, warm, and avoid generic suggestions. Focus on the specific details provided. 
    Format as a brief list. Do not use markdown headers.
  `;

  try {
    const response = await ai.models.generateContent({
      model: 'gemini-3-flash-preview',
      contents: prompt,
    });
    return response.text;
  } catch (error) {
    console.error("Gemini Error:", error);
    return "I couldn't generate insights right now. Please try again later.";
  }
};

export const searchGifts = async (query: string) => {
  try {
    const response = await ai.models.generateContent({
      model: "gemini-3-flash-preview",
      contents: `Find real gift recommendations or shops for: ${query}. Focus on small, high-quality, or artisanal options if possible.`,
      config: {
        tools: [{ googleSearch: {} }],
      },
    });

    const chunks = response.candidates?.[0]?.groundingMetadata?.groundingChunks || [];
    const text = response.text;

    return { text, links: chunks };
  } catch (error) {
    console.error("Search grounding error:", error);
    return { text: "Search currently unavailable.", links: [] };
  }
};

import React, { useState, useEffect } from 'react';
import { Person, Preference, ImportantDate } from './types';
import { 
  Heart, 
  Users, 
  Calendar, 
  Plus, 
  Search, 
  Settings, 
  ChevronRight, 
  ArrowLeft,
  Trash2,
  Gift,
  Coffee,
  Sparkles,
  Info
} from 'lucide-react';
import { getRelationshipAdvice, searchGifts } from './services/geminiService';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'journal' | 'calendar' | 'manifesto'>('journal');
  const [people, setPeople] = useState<Person[]>(() => {
    const saved = localStorage.getItem('kokoro_people');
    return saved ? JSON.parse(saved) : [];
  });
  const [selectedPersonId, setSelectedPersonId] = useState<string | null>(null);
  const [showAddModal, setShowAddModal] = useState(false);

  useEffect(() => {
    localStorage.setItem('kokoro_people', JSON.stringify(people));
  }, [people]);

  const addPerson = (name: string, relationship: string) => {
    const newPerson: Person = {
      id: crypto.randomUUID(),
      name,
      relationship,
      preferences: [],
      dates: [],
      notes: '',
      avatar: `https://picsum.photos/seed/${name}/200`,
    };
    setPeople([...people, newPerson]);
    setShowAddModal(false);
  };

  const deletePerson = (id: string) => {
    if (confirm("Remove this person from your circle? This cannot be undone.")) {
      setPeople(people.filter(p => p.id !== id));
      setSelectedPersonId(null);
    }
  };

  const addPreference = (personId: string, category: Preference['category'], content: string) => {
    setPeople(people.map(p => {
      if (p.id === personId) {
        return {
          ...p,
          preferences: [...p.preferences, { id: crypto.randomUUID(), category, content, timestamp: Date.now() }]
        };
      }
      return p;
    }));
  };

  const addDate = (personId: string, label: string, date: string) => {
    setPeople(people.map(p => {
      if (p.id === personId) {
        return {
          ...p,
          dates: [...p.dates, { id: crypto.randomUUID(), label, date, recurring: true }]
        };
      }
      return p;
    }));
  };

  const selectedPerson = people.find(p => p.id === selectedPersonId);

  return (
    <div className="min-h-screen max-w-2xl mx-auto flex flex-col relative pb-24">
      {/* Header */}
      <header className="p-6 flex items-center justify-between sticky top-0 bg-[#faf9f6]/90 backdrop-blur-md z-10">
        <div className="flex items-center gap-2" onClick={() => { setSelectedPersonId(null); setActiveTab('journal'); }}>
          <Heart className="w-6 h-6 text-rose-400 fill-rose-50" />
          <h1 className="text-2xl font-serif font-semibold tracking-tight">Kokoro</h1>
        </div>
        <button 
          onClick={() => setShowAddModal(true)}
          className="w-10 h-10 rounded-full bg-stone-900 text-white flex items-center justify-center hover:bg-stone-800 transition-colors shadow-lg"
        >
          <Plus className="w-5 h-5" />
        </button>
      </header>

      {/* Main Content Area */}
      <main className="px-6 flex-1 overflow-y-auto">
        {!selectedPersonId ? (
          <div className="space-y-12 py-4">
            {activeTab === 'journal' && (
              <section className="animate-in fade-in slide-in-from-bottom-4 duration-700">
                <div className="mb-8">
                  <h2 className="text-3xl mb-2">The Inner Circle</h2>
                  <p className="text-stone-500 font-light">Your living archive of connection.</p>
                </div>
                {people.length === 0 ? (
                  <div className="py-20 text-center border-2 border-dashed border-stone-200 rounded-2xl">
                    <Users className="w-12 h-12 text-stone-300 mx-auto mb-4" />
                    <p className="text-stone-400">Start by adding someone who matters to you.</p>
                  </div>
                ) : (
                  <div className="grid gap-4">
                    {people.map(person => (
                      <div 
                        key={person.id}
                        onClick={() => setSelectedPersonId(person.id)}
                        className="kokoro-card rounded-2xl p-4 flex items-center gap-4 cursor-pointer hover:border-stone-300 transition-all group"
                      >
                        <img src={person.avatar} className="w-16 h-16 rounded-full object-cover border-2 border-white shadow-sm" />
                        <div className="flex-1">
                          <h3 className="text-lg font-medium">{person.name}</h3>
                          <p className="text-stone-400 text-sm">{person.relationship}</p>
                        </div>
                        <ChevronRight className="w-5 h-5 text-stone-300 group-hover:text-stone-500 transition-colors" />
                      </div>
                    ))}
                  </div>
                )}
              </section>
            )}

            {activeTab === 'calendar' && (
              <section className="animate-in fade-in duration-700">
                <div className="mb-8">
                  <h2 className="text-3xl mb-2">Meaningful Dates</h2>
                  <p className="text-stone-500 font-light">Moments that transform dates into rituals.</p>
                </div>
                <div className="space-y-4">
                  {people.flatMap(p => p.dates.map(d => ({ ...d, personName: p.name }))).length === 0 ? (
                    <p className="text-stone-400 italic">No dates tracked yet.</p>
                  ) : (
                    people.flatMap(p => p.dates.map(d => ({ ...d, personName: p.name })))
                      .sort((a,b) => a.date.localeCompare(b.date))
                      .map(d => (
                        <div key={d.id} className="flex items-center gap-4 bg-white p-4 rounded-xl border border-stone-100 shadow-sm">
                          <div className="w-12 h-12 bg-rose-50 text-rose-400 rounded-lg flex flex-col items-center justify-center font-bold text-xs">
                             <span className="uppercase">{new Date(d.date).toLocaleString('default', { month: 'short' })}</span>
                             <span className="text-lg">{new Date(d.date).getDate()}</span>
                          </div>
                          <div>
                            <p className="font-medium">{d.label}</p>
                            <p className="text-sm text-stone-400">for {d.personName}</p>
                          </div>
                        </div>
                      ))
                  )}
                </div>
              </section>
            )}

            {activeTab === 'manifesto' && (
              <article className="prose prose-stone animate-in fade-in duration-700">
                <h2 className="text-3xl mb-6">The Kokoro Manifesto</h2>
                <div className="space-y-6 text-stone-600 leading-relaxed font-light">
                  <p>1. <strong>Free.</strong> Kokoro exists to help people care for each other, not to trap them in subscriptions.</p>
                  <p>2. <strong>Privacy is Sacred.</strong> We never track, harvest, or sell your data. Every insight is rooted only in what you explicitly share.</p>
                  <p>3. <strong>The Journal is the Heart.</strong> This is not a social network. It is a private emotional assistant for those who truly matter.</p>
                  <p>4. <strong>Quiet & Intentional.</strong> No infinite scrolls. No dopamine traps. Just human connection.</p>
                </div>
              </article>
            )}
          </div>
        ) : (
          <PersonProfile 
            person={selectedPerson!} 
            onBack={() => setSelectedPersonId(null)} 
            onAddPref={addPreference}
            onAddDate={addDate}
            onDelete={deletePerson}
          />
        )}
      </main>

      {/* Tab Navigation */}
      {!selectedPersonId && (
        <nav className="fixed bottom-8 left-1/2 -translate-x-1/2 bg-stone-900 text-stone-400 flex items-center gap-1 p-1 rounded-full shadow-2xl z-20 border border-white/10">
          <button 
            onClick={() => setActiveTab('journal')}
            className={`px-6 py-2 rounded-full transition-all flex items-center gap-2 ${activeTab === 'journal' ? 'bg-stone-700 text-white' : 'hover:text-white'}`}
          >
            <Users className="w-4 h-4" />
            <span className="text-sm font-medium">Circle</span>
          </button>
          <button 
            onClick={() => setActiveTab('calendar')}
            className={`px-6 py-2 rounded-full transition-all flex items-center gap-2 ${activeTab === 'calendar' ? 'bg-stone-700 text-white' : 'hover:text-white'}`}
          >
            <Calendar className="w-4 h-4" />
            <span className="text-sm font-medium">Rituals</span>
          </button>
          <button 
            onClick={() => setActiveTab('manifesto')}
            className={`px-6 py-2 rounded-full transition-all flex items-center gap-2 ${activeTab === 'manifesto' ? 'bg-stone-700 text-white' : 'hover:text-white'}`}
          >
            <Info className="w-4 h-4" />
            <span className="text-sm font-medium">Ethos</span>
          </button>
        </nav>
      )}

      {/* Add Person Modal */}
      {showAddModal && (
        <div className="fixed inset-0 bg-stone-900/40 backdrop-blur-sm flex items-center justify-center p-6 z-50">
          <div className="bg-white rounded-3xl w-full max-w-md p-8 animate-in zoom-in-95 duration-200">
            <h2 className="text-2xl mb-6">New Companion</h2>
            <form onSubmit={(e) => {
              e.preventDefault();
              const formData = new FormData(e.currentTarget);
              addPerson(formData.get('name') as string, formData.get('rel') as string);
            }} className="space-y-4">
              <div>
                <label className="block text-sm text-stone-500 mb-1">Name</label>
                <input required name="name" type="text" className="w-full px-4 py-3 rounded-xl bg-stone-50 border border-stone-100 focus:outline-none focus:ring-2 focus:ring-rose-200 transition-all" />
              </div>
              <div>
                <label className="block text-sm text-stone-500 mb-1">Relationship (e.g. Daughter, Best Friend)</label>
                <input required name="rel" type="text" className="w-full px-4 py-3 rounded-xl bg-stone-50 border border-stone-100 focus:outline-none focus:ring-2 focus:ring-rose-200 transition-all" />
              </div>
              <div className="flex gap-3 pt-4">
                <button type="button" onClick={() => setShowAddModal(false)} className="flex-1 py-3 text-stone-500 font-medium">Cancel</button>
                <button type="submit" className="flex-1 py-3 bg-stone-900 text-white rounded-xl font-medium shadow-lg">Save Profile</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

const PersonProfile: React.FC<{ 
  person: Person, 
  onBack: () => void,
  onAddPref: (pid: string, cat: Preference['category'], content: string) => void,
  onAddDate: (pid: string, label: string, date: string) => void,
  onDelete: (pid: string) => void
}> = ({ person, onBack, onAddPref, onAddDate, onDelete }) => {
  const [newPref, setNewPref] = useState('');
  const [prefCat, setPrefCat] = useState<Preference['category']>('like');
  const [newDateLabel, setNewDateLabel] = useState('');
  const [newDateValue, setNewDateValue] = useState('');
  const [insights, setInsights] = useState<string | null>(null);
  const [loadingInsights, setLoadingInsights] = useState(false);
  const [searchResults, setSearchResults] = useState<{text: string, links: any[]} | null>(null);

  const handleAddPref = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newPref.trim()) return;
    onAddPref(person.id, prefCat, newPref);
    setNewPref('');
  };

  const handleAddDate = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newDateLabel.trim() || !newDateValue) return;
    onAddDate(person.id, newDateLabel, newDateValue);
    setNewDateLabel('');
    setNewDateValue('');
  };

  const fetchInsights = async () => {
    setLoadingInsights(true);
    const res = await getRelationshipAdvice(person);
    setInsights(res);
    setLoadingInsights(false);
  };

  const handleGetItDone = async (query: string) => {
    setLoadingInsights(true);
    const res = await searchGifts(`${person.name} likes ${query}. Suggest real products/places.`);
    setSearchResults(res);
    setLoadingInsights(false);
  };

  return (
    <div className="py-4 animate-in fade-in slide-in-from-right-4 duration-500">
      <button onClick={onBack} className="mb-6 flex items-center gap-2 text-stone-500 hover:text-stone-900 transition-colors">
        <ArrowLeft className="w-4 h-4" />
        <span className="text-sm">Back to Circle</span>
      </button>

      <div className="flex items-center gap-6 mb-12">
        <img src={person.avatar} className="w-24 h-24 rounded-full border-4 border-white shadow-md" />
        <div>
          <h2 className="text-4xl font-serif mb-1">{person.name}</h2>
          <p className="text-stone-500 flex items-center gap-2">
            <Sparkles className="w-4 h-4 text-rose-300" />
            {person.relationship}
          </p>
        </div>
        <button 
          onClick={() => onDelete(person.id)} 
          className="ml-auto p-2 text-stone-300 hover:text-red-400 transition-colors"
          title="Remove person"
        >
          <Trash2 className="w-5 h-5" />
        </button>
      </div>

      <div className="space-y-12">
        {/* Preferences / Facts */}
        <section>
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-xl">Nuances & Notes</h3>
            <div className="flex bg-stone-100 p-1 rounded-lg text-xs font-medium">
              <button onClick={() => setPrefCat('like')} className={`px-2 py-1 rounded ${prefCat === 'like' ? 'bg-white shadow-sm' : ''}`}>Likes</button>
              <button onClick={() => setPrefCat('dislike')} className={`px-2 py-1 rounded ${prefCat === 'dislike' ? 'bg-white shadow-sm' : ''}`}>Avoids</button>
              <button onClick={() => setPrefCat('ritual')} className={`px-2 py-1 rounded ${prefCat === 'ritual' ? 'bg-white shadow-sm' : ''}`}>Rituals</button>
            </div>
          </div>

          <form onSubmit={handleAddPref} className="mb-6 relative">
            <input 
              value={newPref}
              onChange={(e) => setNewPref(e.target.value)}
              placeholder={`e.g. "Loves oolong tea" or "Dislikes surprise parties"`}
              className="w-full pl-4 pr-12 py-3 bg-white border border-stone-200 rounded-xl focus:ring-2 focus:ring-rose-100 outline-none transition-all shadow-sm"
            />
            <button type="submit" className="absolute right-2 top-2 w-8 h-8 flex items-center justify-center bg-stone-900 text-white rounded-lg">
              <Plus className="w-4 h-4" />
            </button>
          </form>

          <div className="flex flex-wrap gap-2">
            {person.preferences.map(pref => (
              <div key={pref.id} className={`px-3 py-1.5 rounded-full text-sm border flex items-center gap-2 ${
                pref.category === 'like' ? 'bg-rose-50 border-rose-100 text-rose-600' :
                pref.category === 'dislike' ? 'bg-stone-100 border-stone-200 text-stone-600' :
                'bg-blue-50 border-blue-100 text-blue-600'
              }`}>
                {pref.category === 'like' && <Heart className="w-3 h-3 fill-rose-600" />}
                {pref.category === 'dislike' && <Coffee className="w-3 h-3" />}
                {pref.content}
                {pref.category === 'like' && (
                  <button 
                    onClick={() => handleGetItDone(pref.content)}
                    className="ml-1 opacity-60 hover:opacity-100 transition-opacity"
                    title="Find gifts"
                  >
                    <Gift className="w-3 h-3" />
                  </button>
                )}
              </div>
            ))}
          </div>
        </section>

        {/* Dates */}
        <section>
          <h3 className="text-xl mb-4">Rituals & Milestones</h3>
          <form onSubmit={handleAddDate} className="flex gap-2 mb-6">
            <input 
              required
              value={newDateLabel}
              onChange={(e) => setNewDateLabel(e.target.value)}
              placeholder="Label (e.g. Birthday)"
              className="flex-1 px-4 py-3 bg-white border border-stone-200 rounded-xl outline-none"
            />
            <input 
              required
              type="date"
              value={newDateValue}
              onChange={(e) => setNewDateValue(e.target.value)}
              className="px-4 py-3 bg-white border border-stone-200 rounded-xl outline-none"
            />
            <button type="submit" className="px-4 bg-stone-900 text-white rounded-xl">
              Add
            </button>
          </form>

          <div className="space-y-3">
            {person.dates.map(date => (
              <div key={date.id} className="flex justify-between items-center p-3 border-b border-stone-100 font-light">
                <span>{date.label}</span>
                <span className="text-stone-400">{new Date(date.date).toLocaleDateString()}</span>
              </div>
            ))}
          </div>
        </section>

        {/* AI Assistant Context Area */}
        <section className="bg-rose-50/50 rounded-3xl p-6 border border-rose-100/50">
          <div className="flex items-center gap-2 mb-4">
            <Sparkles className="w-5 h-5 text-rose-400" />
            <h3 className="text-lg font-medium text-rose-900">Thoughtful Spark</h3>
          </div>

          {!insights && !searchResults && (
            <p className="text-sm text-stone-500 mb-6 font-light">
              Based on the nuances you've shared, I can suggest meaningful ways to show up for {person.name}.
            </p>
          )}

          {insights && !searchResults && (
            <div className="bg-white/80 backdrop-blur-sm p-4 rounded-2xl mb-6 text-sm text-stone-700 whitespace-pre-wrap leading-relaxed shadow-sm">
              {insights}
            </div>
          )}

          {searchResults && (
            <div className="bg-white/80 backdrop-blur-sm p-4 rounded-2xl mb-6 shadow-sm">
              <h4 className="font-medium text-stone-900 mb-2">Gift Explorer</h4>
              <p className="text-sm text-stone-700 mb-4">{searchResults.text}</p>
              {searchResults.links.length > 0 && (
                <div className="space-y-2 border-t pt-2">
                  <p className="text-[10px] uppercase tracking-wider text-stone-400 font-bold">Suggested Sources</p>
                  {searchResults.links.map((link: any, idx: number) => (
                    link.web && (
                      <a 
                        key={idx} 
                        href={link.web.uri} 
                        target="_blank" 
                        rel="noopener noreferrer" 
                        className="block text-xs text-rose-500 hover:underline truncate"
                      >
                        {link.web.title || link.web.uri}
                      </a>
                    )
                  ))}
                </div>
              )}
              <button 
                onClick={() => setSearchResults(null)}
                className="mt-4 text-[10px] text-stone-400 hover:text-stone-600 font-bold uppercase tracking-widest"
              >
                Clear Results
              </button>
            </div>
          )}

          <div className="flex gap-2">
            <button 
              disabled={loadingInsights || person.preferences.length === 0}
              onClick={fetchInsights}
              className="flex-1 py-3 bg-white border border-rose-200 text-rose-600 rounded-xl font-medium text-sm hover:bg-rose-100 transition-colors disabled:opacity-50"
            >
              {loadingInsights ? 'Reflecting...' : 'Suggest Gestures'}
            </button>
          </div>
          {person.preferences.length === 0 && (
            <p className="text-[10px] text-stone-400 mt-2 text-center">Add some likes or rituals to unlock suggestions.</p>
          )}
        </section>
      </div>
    </div>
  );
};

export default App;

export type CardStatus = 'PLANNED' | 'ORDERED' | 'SENT' | 'DELETED';

export interface Preference {
  id: string;
  category: 'like' | 'dislike' | 'fact' | 'ritual';
  content: string;
  timestamp: number;
}

export interface ImportantDate {
  id: string;
  label: string;
  date: string; // ISO format
  recurring: boolean;
  status: CardStatus;
  leadDays: number;
  notes?: string;
}

export interface Person {
  id: string;
  name: string;
  nickname?: string;
  relationship: string;
  avatar?: string;
  phone?: string;
  email?: string;
  address?: string;
  pinned: boolean;
  preferences: Preference[];
  dates: ImportantDate[];
  notes: string;
  lastInteraction?: number;
}

import React, { useState, useEffect, useMemo } from 'react';
import { Person, Preference, ImportantDate, CardStatus } from './types';
import { 
  Heart, Users, Calendar, Plus, ChevronRight, ArrowLeft, Trash2, 
  Gift, Coffee, Sparkles, Info, Settings, Star, ChevronLeft,
  Phone, Mail, MapPin, Bell
} from 'lucide-react';
import { getRelationshipAdvice } from './services/geminiService';

const THEMES = ['earthy', 'pastel', 'dark', 'bay'];

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'journal' | 'calendar' | 'manifesto' | 'settings'>('journal');
  const [theme, setTheme] = useState(() => localStorage.getItem('kokoro_theme') || 'earthy');
  const [people, setPeople] = useState<Person[]>(() => {
    const saved = localStorage.getItem('kokoro_people');
    return saved ? JSON.parse(saved) : [];
  });
  const [selectedPersonId, setSelectedPersonId] = useState<string | null>(null);
  const [showAddModal, setShowAddModal] = useState(false);

  useEffect(() => {
    localStorage.setItem('kokoro_people', JSON.stringify(people));
  }, [people]);

  useEffect(() => {
    document.body.className = `theme-${theme}`;
    localStorage.setItem('kokoro_theme', theme);
  }, [theme]);

  const sortedPeople = useMemo(() => {
    return [...people].sort((a, b) => {
      if (a.pinned && !b.pinned) return -1;
      if (!a.pinned && b.pinned) return 1;
      return a.name.localeCompare(b.name);
    });
  }, [people]);

  const upcomingEvents = useMemo(() => {
    const allEvents = people.flatMap(p => 
      p.dates.map(d => ({ ...d, personName: p.name, personId: p.id }))
    );
    const today = new Date();
    return allEvents
      .filter(e => new Date(e.date) >= today || e.recurring)
      .sort((a, b) => new Date(a.date).getTime() - new Date(b.date).getTime())
      .slice(0, 5);
  }, [people]);

  const addPerson = (name: string, relationship: string) => {
    const newPerson: Person = {
      id: crypto.randomUUID(),
      name,
      relationship,
      pinned: false,
      preferences: [],
      dates: [],
      notes: '',
      avatar: `https://picsum.photos/seed/${name}/200`,
    };
    setPeople([...people, newPerson]);
    setShowAddModal(false);
  };

  const updatePerson = (id: string, updates: Partial<Person>) => {
    setPeople(prev => prev.map(p => p.id === id ? { ...p, ...updates } : p));
  };

  const deletePerson = (id: string) => {
    if (confirm("Remove this person from your circle?")) {
      setPeople(people.filter(p => p.id !== id));
      setSelectedPersonId(null);
    }
  };

  const selectedPerson = people.find(p => p.id === selectedPersonId);

  return (
    <div className="min-h-screen max-w-2xl mx-auto flex flex-col relative pb-24 font-sans text-[var(--ink)]">
      {/* Header */}
      <header className="p-6 flex items-center justify-between sticky top-0 bg-[var(--bg)] bg-opacity-90 backdrop-blur-md z-30">
        <div className="flex items-center gap-2 cursor-pointer" onClick={() => { setSelectedPersonId(null); setActiveTab('journal'); }}>
          <Heart className="w-6 h-6 text-[var(--primary)] fill-[var(--primary)] opacity-80" />
          <h1 className="text-2xl font-serif font-semibold tracking-tight">Kokoro</h1>
        </div>
        <div className="flex gap-4">
          <button onClick={() => setActiveTab('settings')} className="p-2 rounded-full hover:bg-[var(--soft)] transition-colors">
            <Settings className="w-5 h-5 opacity-60" />
          </button>
          <button onClick={() => setShowAddModal(true)} className="w-10 h-10 rounded-full bg-[var(--ink)] text-[var(--card)] flex items-center justify-center hover:opacity-90 transition-all shadow-lg">
            <Plus className="w-5 h-5" />
          </button>
        </div>
      </header>

      {/* Main Content Area */}
      <main className="px-6 flex-1 overflow-y-auto custom-scrollbar">
        {!selectedPersonId ? (
          <div className="space-y-12 py-4">
            {activeTab === 'journal' && (
              <section className="animate-in fade-in slide-in-from-bottom-4 duration-700">
                {upcomingEvents.length > 0 && (
                  <div className="mb-10">
                    <h3 className="text-sm font-bold uppercase tracking-widest opacity-40 mb-4">Upcoming Rituals</h3>
                    <div className="flex gap-4 overflow-x-auto pb-4 no-scrollbar">
                      {upcomingEvents.map(e => (
                        <div key={e.id} onClick={() => setSelectedPersonId(e.personId)} className="kokoro-card min-w-[200px] p-4 rounded-2xl cursor-pointer hover:translate-y-[-2px] transition-transform">
                          <p className="text-xs font-bold text-[var(--primary)] mb-1">{e.label}</p>
                          <p className="font-medium">{e.personName}</p>
                          <p className="text-xs opacity-50 mt-2">{new Date(e.date).toLocaleDateString(undefined, { month: 'short', day: 'numeric' })}</p>
                          <div className={`status-chip status-${e.status} mt-3 inline-block`}>{e.status}</div>
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                <div className="mb-8">
                  <h2 className="text-3xl mb-2">The Inner Circle</h2>
                  <p className="opacity-50 font-light">Your living archive of connection.</p>
                </div>

                {people.length === 0 ? (
                  <div className="py-20 text-center border-2 border-dashed border-[var(--soft)] rounded-3xl">
                    <Users className="w-12 h-12 opacity-20 mx-auto mb-4" />
                    <p className="opacity-40">Start by adding someone who matters to you.</p>
                  </div>
                ) : (
                  <div className="grid gap-4">
                    {sortedPeople.map(person => (
                      <div 
                        key={person.id}
                        onClick={() => setSelectedPersonId(person.id)}
                        className="kokoro-card rounded-2xl p-4 flex items-center gap-4 cursor-pointer hover:border-[var(--primary)] transition-all group relative"
                      >
                        <img src={person.avatar} className="w-16 h-16 rounded-full object-cover border-2 border-[var(--card)] shadow-sm" />
                        <div className="flex-1">
                          <div className="flex items-center gap-2">
                            <h3 className="text-lg font-medium">{person.name}</h3>
                            {person.pinned && <Star className="w-3 h-3 text-[var(--primary)] fill-[var(--primary)]" />}
                          </div>
                          <p className="opacity-50 text-sm">{person.relationship}</p>
                        </div>
                        <ChevronRight className="w-5 h-5 opacity-20 group-hover:opacity-100 transition-colors" />
                      </div>
                    ))}
                  </div>
                )}
              </section>
            )}

            {activeTab === 'calendar' && (
              <section className="animate-in fade-in duration-700">
                <div className="mb-8">
                  <h2 className="text-3xl mb-2">The Ritual Calendar</h2>
                  <p className="opacity-50 font-light">Moments that transform dates into meaning.</p>
                </div>
                <CalendarView people={people} onSelectPerson={setSelectedPersonId} />
              </section>
            )}

            {activeTab === 'settings' && (
              <section className="animate-in fade-in duration-500">
                <h2 className="text-3xl mb-8">Settings</h2>
                <div className="space-y-6">
                  <div>
                    <label className="block text-sm font-bold uppercase opacity-40 mb-3">App Theme</label>
                    <div className="grid grid-cols-2 gap-3">
                      {THEMES.map(t => (
                        <button 
                          key={t}
                          onClick={() => setTheme(t)}
                          className={`p-4 rounded-2xl border-2 transition-all capitalize ${theme === t ? 'border-[var(--primary)] bg-[var(--card)]' : 'border-transparent bg-[var(--soft)] bg-opacity-30'}`}
                        >
                          {t}
                        </button>
                      ))}
                    </div>
                  </div>
                  <div className="pt-8 border-t border-[var(--soft)]">
                    <p className="text-sm opacity-50 mb-4">Kokoro v1.2 — Private & Local Data</p>
                    <button onClick={() => { if(confirm("Export all data?")) console.log(people) }} className="w-full py-3 bg-[var(--soft)] bg-opacity-50 rounded-xl text-sm font-medium">Export Data (JSON)</button>
                  </div>
                </div>
              </section>
            )}

            {activeTab === 'manifesto' && (
              <article className="prose prose-stone animate-in fade-in duration-700 text-[var(--ink)]">
                <h2 className="text-3xl mb-6 font-serif">The Kokoro Ethos</h2>
                <div className="space-y-6 opacity-80 leading-relaxed font-light">
                  <p>1. <strong>Quiet & Intentional.</strong> Connection should not be a competition for attention.</p>
                  <p>2. <strong>Privacy is Sacred.</strong> Your relationships are not data points. Everything stays on your device.</p>
                  <p>3. <strong>The Small Details.</strong> A favorite coffee order is as important as a birthday.</p>
                </div>
              </article>
            )}
          </div>
        ) : (
          <PersonProfile 
            person={selectedPerson!} 
            onBack={() => setSelectedPersonId(null)} 
            onUpdate={(updates) => updatePerson(selectedPerson!.id, updates)}
            onDelete={deletePerson}
          />
        )}
      </main>

      {/* Tab Navigation */}
      {!selectedPersonId && (
        <nav className="fixed bottom-8 left-1/2 -translate-x-1/2 bg-[var(--ink)] text-[var(--bg)] flex items-center gap-1 p-1 rounded-full shadow-2xl z-40">
          <button 
            onClick={() => setActiveTab('journal')}
            className={`px-6 py-2 rounded-full transition-all flex items-center gap-2 ${activeTab === 'journal' ? 'bg-[var(--primary)] text-[var(--bg)]' : 'opacity-60 hover:opacity-100'}`}
          >
            <Users className="w-4 h-4" />
            <span className="text-sm font-medium">Circle</span>
          </button>
          <button 
            onClick={() => setActiveTab('calendar')}
            className={`px-6 py-2 rounded-full transition-all flex items-center gap-2 ${activeTab === 'calendar' ? 'bg-[var(--primary)] text-[var(--bg)]' : 'opacity-60 hover:opacity-100'}`}
          >
            <Calendar className="w-4 h-4" />
            <span className="text-sm font-medium">Rituals</span>
          </button>
          <button 
            onClick={() => setActiveTab('manifesto')}
            className={`px-6 py-2 rounded-full transition-all flex items-center gap-2 ${activeTab === 'manifesto' ? 'bg-[var(--primary)] text-[var(--bg)]' : 'opacity-60 hover:opacity-100'}`}
          >
            <Info className="w-4 h-4" />
            <span className="text-sm font-medium">Ethos</span>
          </button>
        </nav>
      )}

      {/* Add Person Modal */}
      {showAddModal && (
        <div className="fixed inset-0 bg-black bg-opacity-40 backdrop-blur-sm flex items-center justify-center p-6 z-50">
          <div className="bg-[var(--card)] text-[var(--ink)] rounded-3xl w-full max-w-md p-8 animate-in zoom-in-95 duration-200">
            <h2 className="text-2xl font-serif mb-6">New Companion</h2>
            <form onSubmit={(e) => {
              e.preventDefault();
              const formData = new FormData(e.currentTarget);
              addPerson(formData.get('name') as string, formData.get('rel') as string);
            }} className="space-y-4">
              <div>
                <label className="block text-xs uppercase opacity-40 font-bold mb-1">Name</label>
                <input required name="name" type="text" className="w-full px-4 py-3 rounded-xl bg-[var(--bg)] border border-[var(--soft)] focus:outline-none focus:ring-2 focus:ring-[var(--primary)] transition-all" />
              </div>
              <div>
                <label className="block text-xs uppercase opacity-40 font-bold mb-1">Relationship</label>
                <input required name="rel" type="text" className="w-full px-4 py-3 rounded-xl bg-[var(--bg)] border border-[var(--soft)] focus:outline-none focus:ring-2 focus:ring-[var(--primary)] transition-all" />
              </div>
              <div className="flex gap-3 pt-4">
                <button type="button" onClick={() => setShowAddModal(false)} className="flex-1 py-3 opacity-50 font-medium">Cancel</button>
                <button type="submit" className="flex-1 py-3 bg-[var(--ink)] text-[var(--bg)] rounded-xl font-medium shadow-lg">Save Profile</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

const CalendarView: React.FC<{ people: Person[], onSelectPerson: (id: string) => void }> = ({ people, onSelectPerson }) => {
  const [viewDate, setViewDate] = useState(new Date());

  const daysInMonth = new Date(viewDate.getFullYear(), viewDate.getMonth() + 1, 0).getDate();
  const firstDay = new Date(viewDate.getFullYear(), viewDate.getMonth(), 1).getDay();

  const monthEvents = useMemo(() => {
    return people.flatMap(p => 
      p.dates.map(d => ({ ...d, personId: p.id, dObj: new Date(d.date) }))
    ).filter(e => e.dObj.getMonth() === viewDate.getMonth());
  }, [people, viewDate]);

  return (
    <div className="kokoro-card p-6 rounded-3xl">
      <div className="flex items-center justify-between mb-8">
        <button onClick={() => setViewDate(new Date(viewDate.setMonth(viewDate.getMonth() - 1)))} className="p-2 rounded-lg bg-[var(--bg)]"><ChevronLeft className="w-4 h-4"/></button>
        <h3 className="font-serif text-xl">{viewDate.toLocaleDateString(undefined, { month: 'long', year: 'numeric' })}</h3>
        <button onClick={() => setViewDate(new Date(viewDate.setMonth(viewDate.getMonth() + 1)))} className="p-2 rounded-lg bg-[var(--bg)]"><ChevronRight className="w-4 h-4"/></button>
      </div>
      <div className="grid grid-cols-7 gap-2">
        {['S','M','T','W','T','F','S'].map(d => <div key={d} className="text-center text-[10px] font-bold opacity-30">{d}</div>)}
        {Array.from({ length: firstDay }).map((_, i) => <div key={`empty-${i}`} />)}
        {Array.from({ length: daysInMonth }).map((_, i) => {
          const day = i + 1;
          const hasEvent = monthEvents.find(e => e.dObj.getDate() === day);
          return (
            <div 
              key={day} 
              onClick={() => hasEvent && onSelectPerson(hasEvent.personId)}
              className={`aspect-square flex flex-col items-center justify-center rounded-xl text-sm transition-all cursor-pointer ${hasEvent ? 'bg-[var(--primary)] text-[var(--bg)]' : 'hover:bg-[var(--bg)]'}`}
            >
              {day}
              {hasEvent && <div className="w-1 h-1 bg-white rounded-full mt-1" />}
            </div>
          );
        })}
      </div>
    </div>
  );
};

const PersonProfile: React.FC<{ 
  person: Person, 
  onBack: () => void,
  onUpdate: (updates: Partial<Person>) => void,
  onDelete: (pid: string) => void
}> = ({ person, onBack, onUpdate, onDelete }) => {
  const [insights, setInsights] = useState<string | null>(null);
  const [loadingInsights, setLoadingInsights] = useState(false);
  const [newPref, setNewPref] = useState('');
  const [newDateLabel, setNewDateLabel] = useState('');
  const [newDateValue, setNewDateValue] = useState('');

  const handleUpdateField = (field: keyof Person, value: any) => {
    onUpdate({ [field]: value });
  };

  const addPref = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newPref.trim()) return;
    const pref: Preference = { id: crypto.randomUUID(), category: 'like', content: newPref, timestamp: Date.now() };
    onUpdate({ preferences: [...person.preferences, pref] });
    setNewPref('');
  };

  const addDate = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newDateLabel || !newDateValue) return;
    const date: ImportantDate = { id: crypto.randomUUID(), label: newDateLabel, date: newDateValue, recurring: true, status: 'PLANNED', leadDays: 7 };
    onUpdate({ dates: [...person.dates, date] });
    setNewDateLabel('');
    setNewDateValue('');
  };

  const fetchInsights = async () => {
    setLoadingInsights(true);
    const res = await getRelationshipAdvice(person);
    setInsights(res);
    setLoadingInsights(false);
  };

  return (
    <div className="py-4 animate-in fade-in slide-in-from-right-4 duration-500">
      <div className="flex justify-between items-center mb-6">
        <button onClick={onBack} className="flex items-center gap-2 opacity-50 hover:opacity-100 transition-colors">
          <ArrowLeft className="w-4 h-4" />
          <span className="text-sm">Back to Circle</span>
        </button>
        <button onClick={() => handleUpdateField('pinned', !person.pinned)} className="p-2">
          <Star className={`w-5 h-5 ${person.pinned ? 'text-[var(--primary)] fill-[var(--primary)]' : 'opacity-20'}`} />
        </button>
      </div>

      <div className="flex items-center gap-6 mb-12">
        <img src={person.avatar} className="w-24 h-24 rounded-full border-4 border-[var(--card)] shadow-md" />
        <div>
          <h2 className="text-4xl font-serif mb-1">{person.name}</h2>
          <p className="opacity-50 flex items-center gap-2">
            <Sparkles className="w-4 h-4 text-[var(--primary)]" />
            {person.relationship}
          </p>
        </div>
      </div>

      <div className="space-y-12">
        {/* Contact Details Dashboard Style */}
        <section className="kokoro-card p-6 rounded-3xl space-y-4">
          <h3 className="text-xs uppercase font-bold opacity-30 mb-2">Nuances & Contact</h3>
          <div className="grid grid-cols-1 gap-3">
            <div className="flex items-center gap-3 p-3 bg-[var(--bg)] rounded-xl">
              <Phone className="w-4 h-4 opacity-40" />
              <input 
                placeholder="Phone number" 
                value={person.phone || ''} 
                onChange={(e) => handleUpdateField('phone', e.target.value)}
                className="bg-transparent text-sm w-full outline-none"
              />
            </div>
            <div className="flex items-center gap-3 p-3 bg-[var(--bg)] rounded-xl">
              <Mail className="w-4 h-4 opacity-40" />
              <input 
                placeholder="Email address" 
                value={person.email || ''} 
                onChange={(e) => handleUpdateField('email', e.target.value)}
                className="bg-transparent text-sm w-full outline-none"
              />
            </div>
            <div className="flex items-center gap-3 p-3 bg-[var(--bg)] rounded-xl">
              <MapPin className="w-4 h-4 opacity-40" />
              <input 
                placeholder="Physical address" 
                value={person.address || ''} 
                onChange={(e) => handleUpdateField('address', e.target.value)}
                className="bg-transparent text-sm w-full outline-none"
              />
            </div>
          </div>
        </section>

        {/* Preferences */}
        <section>
          <h3 className="text-xl mb-4 font-serif">Deep Nuances</h3>
          <form onSubmit={addPref} className="mb-4 relative">
            <input 
              value={newPref}
              onChange={(e) => setNewPref(e.target.value)}
              placeholder="Add something they like, dislike, or a shared ritual..."
              className="w-full pl-4 pr-12 py-3 bg-[var(--card)] border border-[var(--soft)] rounded-xl outline-none"
            />
            <button type="submit" className="absolute right-2 top-2 w-8 h-8 flex items-center justify-center bg-[var(--ink)] text-[var(--bg)] rounded-lg"><Plus className="w-4 h-4"/></button>
          </form>
          <div className="flex flex-wrap gap-2">
            {person.preferences.map(p => (
              <div key={p.id} className="px-3 py-1 bg-[var(--soft)] bg-opacity-30 rounded-full text-xs flex items-center gap-2">
                <Coffee className="w-3 h-3 opacity-40" />
                {p.content}
              </div>
            ))}
          </div>
        </section>

        {/* Gestures & Status Tracking */}
        <section>
          <h3 className="text-xl mb-4 font-serif">Cards & Rituals</h3>
          <form onSubmit={addDate} className="grid grid-cols-1 md:grid-cols-3 gap-2 mb-6">
            <input required placeholder="Occasion" value={newDateLabel} onChange={e => setNewDateLabel(e.target.value)} className="px-4 py-2 bg-[var(--card)] border border-[var(--soft)] rounded-xl" />
            <input required type="date" value={newDateValue} onChange={e => setNewDateValue(e.target.value)} className="px-4 py-2 bg-[var(--card)] border border-[var(--soft)] rounded-xl" />
            <button type="submit" className="px-4 py-2 bg-[var(--ink)] text-[var(--bg)] rounded-xl font-medium">Add Card</button>
          </form>
          <div className="space-y-3">
            {person.dates.map(d => (
              <div key={d.id} className="kokoro-card p-4 rounded-2xl flex justify-between items-center">
                <div>
                  <p className="font-medium text-sm">{d.label}</p>
                  <p className="text-[10px] opacity-40 mt-1">{new Date(d.date).toLocaleDateString()}</p>
                </div>
                <select 
                  value={d.status}
                  onChange={(e) => {
                    const newDates = person.dates.map(date => date.id === d.id ? { ...date, status: e.target.value as CardStatus } : date);
                    onUpdate({ dates: newDates });
                  }}
                  className={`status-chip status-${d.status} bg-opacity-10 border-none outline-none cursor-pointer`}
                >
                  <option value="PLANNED">Planned</option>
                  <option value="ORDERED">Ordered</option>
                  <option value="SENT">Sent</option>
                </select>
              </div>
            ))}
          </div>
        </section>

        {/* AI Suggestions */}
        <section className="bg-[var(--primary)] bg-opacity-10 rounded-3xl p-6 border border-[var(--primary)] border-opacity-20">
          <div className="flex items-center gap-2 mb-4">
            <Sparkles className="w-5 h-5 text-[var(--primary)]" />
            <h3 className="text-lg font-medium text-[var(--ink)]">Thoughtful Spark</h3>
          </div>
          {insights ? (
            <div className="bg-[var(--card)] p-4 rounded-2xl mb-6 text-sm opacity-80 whitespace-pre-wrap leading-relaxed shadow-sm">
              {insights}
            </div>
          ) : (
            <p className="text-sm opacity-50 mb-6 font-light">Based on the nuances you've shared, I can suggest meaningful ways to show up for {person.name}.</p>
          )}
          <button 
            disabled={loadingInsights || person.preferences.length === 0}
            onClick={fetchInsights}
            className="w-full py-3 bg-[var(--card)] border border-[var(--primary)] text-[var(--primary)] rounded-xl font-medium text-sm hover:bg-[var(--primary)] hover:text-white transition-all disabled:opacity-30"
          >
            {loadingInsights ? 'Reflecting...' : 'Suggest Gestures'}
          </button>
        </section>

        <button 
          onClick={() => onDelete(person.id)} 
          className="w-full py-4 text-xs font-bold uppercase tracking-widest text-red-400 opacity-50 hover:opacity-100 transition-opacity flex items-center justify-center gap-2"
        >
          <Trash2 className="w-3 h-3" />
          Delete Profile
        </button>
      </div>
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo } from 'react';
import { Person, Preference, ImportantDate, CardStatus } from './types';
import { 
  Heart, Users, Calendar, Plus, ChevronRight, ArrowLeft, Trash2, 
  Gift, Coffee, Sparkles, Info, Settings, Star, ChevronLeft,
  Phone, Mail, MapPin, Bell, User
} from 'lucide-react';
import { getRelationshipAdvice } from './services/geminiService';

const THEMES = ['earthy', 'pastel', 'dark', 'bay'];

const App: React.FC = () => {
  const [view, setView] = useState<'home' | 'rolodex' | 'settings' | 'manifesto'>('home');
  const [theme, setTheme] = useState(() => localStorage.getItem('kokoro_theme') || 'earthy');
  const [people, setPeople] = useState<Person[]>(() => {
    const saved = localStorage.getItem('kokoro_people');
    return saved ? JSON.parse(saved) : [];
  });
  const [selectedPersonId, setSelectedPersonId] = useState<string | null>(null);
  const [showAddModal, setShowAddModal] = useState(false);

  useEffect(() => {
    localStorage.setItem('kokoro_people', JSON.stringify(people));
  }, [people]);

  useEffect(() => {
    document.body.className = `theme-${theme}`;
    localStorage.setItem('kokoro_theme', theme);
  }, [theme]);

  const pinnedPeople = useMemo(() => people.filter(p => p.pinned), [people]);

  const sortedPeople = useMemo(() => {
    return [...people].sort((a, b) => a.name.localeCompare(b.name));
  }, [people]);

  const allEvents = useMemo(() => {
    return people.flatMap(p => 
      p.dates.map(d => ({ ...d, personName: p.name, personId: p.id }))
    ).sort((a, b) => new Date(a.date).getTime() - new Date(b.date).getTime());
  }, [people]);

  const upcomingHero = useMemo(() => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    return allEvents.find(e => new Date(e.date) >= today);
  }, [allEvents]);

  const addPerson = (name: string, relationship: string) => {
    const newPerson: Person = {
      id: crypto.randomUUID(),
      name,
      relationship,
      pinned: false,
      preferences: [],
      dates: [],
      notes: '',
      avatar: `https://picsum.photos/seed/${name}/200`,
    };
    setPeople([...people, newPerson]);
    setShowAddModal(false);
  };

  const updatePerson = (id: string, updates: Partial<Person>) => {
    setPeople(prev => prev.map(p => p.id === id ? { ...p, ...updates } : p));
  };

  const deletePerson = (id: string) => {
    if (confirm("Remove this person from your circle?")) {
      setPeople(people.filter(p => p.id !== id));
      setSelectedPersonId(null);
    }
  };

  const selectedPerson = people.find(p => p.id === selectedPersonId);

  // Home View Component
  const HomeView = () => (
    <div className="space-y-6 animate-in fade-in duration-500">
      {/* Top Cards */}
      <div className="grid grid-cols-2 gap-3">
        <button 
          onClick={() => setView('settings')}
          className="kokoro-card p-4 rounded-2xl flex flex-col items-start gap-1 text-left border-[var(--soft)]"
        >
          <div className="flex items-center gap-2 text-[var(--primary)] font-medium">
            <User className="w-5 h-5" />
            <span>Chris</span>
          </div>
          <span className="text-xs opacity-40">Settings & profile</span>
        </button>
