const CalendarView: React.FC<{ people: Person[], onSelectPerson: (id: string) => void }> = ({ people, onSelectPerson }) => {
  const [viewDate, setViewDate] = useState(new Date());

  const daysInMonth = new Date(viewDate.getFullYear(), viewDate.getMonth() + 1, 0).getDate();
  const firstDay = new Date(viewDate.getFullYear(), viewDate.getMonth(), 1).getDay();

  const monthEvents = useMemo(() => {
    return people.flatMap(p => 
      p.dates.map(d => ({ ...d, personId: p.id, dObj: new Date(d.date) }))
    ).filter(e => e.dObj.getMonth() === viewDate.getMonth() && e.dObj.getFullYear() === viewDate.getFullYear());
  }, [people, viewDate]);

  return (
    <div className="kokoro-card p-6 rounded-2xl border-[var(--soft)]">
      <div className="flex items-center justify-between mb-8">
        <button onClick={() => setViewDate(new Date(viewDate.setMonth(viewDate.getMonth() - 1)))} className="p-2 rounded-lg hover:bg-[var(--bg)] transition-colors"><ChevronLeft className="w-4 h-4"/></button>
        <h3 className="font-serif text-xl font-bold">{viewDate.toLocaleDateString(undefined, { month: 'long', year: 'numeric' })}</h3>
        <button onClick={() => setViewDate(new Date(viewDate.setMonth(viewDate.getMonth() + 1)))} className="p-2 rounded-lg hover:bg-[var(--bg)] transition-colors"><ChevronRight className="w-4 h-4"/></button>
      </div>
      <div className="grid grid-cols-7 gap-1">
        {['Sun','Mon','Tue','Wed','Thu','Fri','Sat'].map(d => <div key={d} className="text-center text-[10px] font-bold opacity-30 py-2">{d}</div>)}
        {Array.from({ length: firstDay }).map((_, i) => (
          <div key={`empty-${i}`} className="aspect-square opacity-10 flex items-center justify-center text-xs">
            {new Date(viewDate.getFullYear(), viewDate.getMonth(), 0).getDate() - firstDay + i + 1}
          </div>
        ))}
        {Array.from({ length: daysInMonth }).map((_, i) => {
          const day = i + 1;
          const hits = monthEvents.filter(e => e.dObj.getDate() === day);
          const isToday = new Date().toDateString() === new Date(viewDate.getFullYear(), viewDate.getMonth(), day).toDateString();
          return (
            <div 
              key={day} 
              onClick={() => hits.length > 0 && onSelectPerson(hits[0].personId)}
              className={`aspect-square relative flex flex-col items-center justify-start pt-2 rounded-lg text-sm border border-transparent transition-all ${hits.length > 0 ? 'cursor-pointer hover:border-[var(--primary)]' : ''} ${isToday ? 'border-[var(--primary)] text-[var(--primary)] font-bold' : 'opacity-70'}`}
            >
              <span>{day}</span>
              <div className="flex gap-0.5 mt-auto mb-1">
                {hits.map((_, idx) => (
                  <div key={idx} className="w-1 h-1 bg-[var(--primary)] rounded-full" />
                ))}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

const PersonProfile: React.FC<{ 
  person: Person, 
  onBack: () => void,
  onUpdate: (updates: Partial<Person>) => void,
  onDelete: (pid: string) => void
}> = ({ person, onBack, onUpdate, onDelete }) => {
  const [insights, setInsights] = useState<string | null>(null);
  const [loadingInsights, setLoadingInsights] = useState(false);
  const [newPref, setNewPref] = useState('');
  const [newDateLabel, setNewDateLabel] = useState('');
  const [newDateValue, setNewDateValue] = useState('');

  const handleUpdateField = (field: keyof Person, value: any) => {
    onUpdate({ [field]: value });
  };

  const addPref = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newPref.trim()) return;
    const pref: Preference = { id: crypto.randomUUID(), category: 'like', content: newPref, timestamp: Date.now() };
    onUpdate({ preferences: [...person.preferences, pref] });
    setNewPref('');
  };

  const addDate = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newDateLabel || !newDateValue) return;
    const date: ImportantDate = { id: crypto.randomUUID(), label: newDateLabel, date: newDateValue, recurring: true, status: 'PLANNED', leadDays: 7 };
    onUpdate({ dates: [...person.dates, date] });
    setNewDateLabel('');
    setNewDateValue('');
  };

  const fetchInsights = async () => {
    setLoadingInsights(true);
    const res = await getRelationshipAdvice(person);
    setInsights(res);
    setLoadingInsights(false);
  };

  return (
    <div className="py-4 animate-in fade-in slide-in-from-right-4 duration-500">
      <div className="flex justify-between items-center mb-6">
        <button onClick={onBack} className="flex items-center gap-2 opacity-50 hover:opacity-100 transition-colors">
          <ArrowLeft className="w-4 h-4" />
          <span className="text-sm">Back Home</span>
        </button>
        <button onClick={() => handleUpdateField('pinned', !person.pinned)} className="p-2 transition-transform active:scale-125">
          <Star className={`w-5 h-5 ${person.pinned ? 'text-[var(--primary)] fill-[var(--primary)]' : 'opacity-20'}`} />
        </button>
      </div>

      <div className="flex items-center gap-6 mb-12">
        <img src={person.avatar} className="w-24 h-24 rounded-full border-4 border-[var(--card)] shadow-lg" />
        <div>
          <h2 className="text-4xl font-serif mb-1">{person.name}</h2>
          <p className="opacity-50 flex items-center gap-2">
            <Sparkles className="w-4 h-4 text-[var(--primary)]" />
            {person.relationship}
          </p>
        </div>
      </div>

      <div className="space-y-12">
        {/* Contact Info */}
        <section className="kokoro-card p-6 rounded-2xl space-y-4 border-[var(--soft)]">
          <h3 className="text-[10px] uppercase font-bold opacity-30 tracking-widest mb-2">Nuances & Contact</h3>
          <div className="grid grid-cols-1 gap-2">
            <div className="flex items-center gap-3 p-3 bg-[var(--bg)] rounded-xl border border-[var(--soft)]">
              <Phone className="w-4 h-4 opacity-40" />
              <input 
                placeholder="Phone number" 
                value={person.phone || ''} 
                onChange={(e) => handleUpdateField('phone', e.target.value)}
                className="bg-transparent text-sm w-full outline-none"
              />
            </div>
            <div className="flex items-center gap-3 p-3 bg-[var(--bg)] rounded-xl border border-[var(--soft)]">
              <Mail className="w-4 h-4 opacity-40" />
              <input 
                placeholder="Email address" 
                value={person.email || ''} 
                onChange={(e) => handleUpdateField('email', e.target.value)}
                className="bg-transparent text-sm w-full outline-none"
              />
            </div>
            <div className="flex items-center gap-3 p-3 bg-[var(--bg)] rounded-xl border border-[var(--soft)]">
              <MapPin className="w-4 h-4 opacity-40" />
              <input 
                placeholder="Physical address" 
                value={person.address || ''} 
                onChange={(e) => handleUpdateField('address', e.target.value)}
                className="bg-transparent text-sm w-full outline-none"
              />
            </div>
          </div>
        </section>

        {/* Nuances/Preferences */}
        <section>
          <h3 className="text-xl mb-4 font-serif font-bold">Deep Nuances</h3>
          <form onSubmit={addPref} className="mb-4 relative">
            <input 
              value={newPref}
              onChange={(e) => setNewPref(e.target.value)}
              placeholder="Add likes, dislikes, or rituals..."
              className="w-full pl-4 pr-12 py-3 bg-[var(--card)] border border-[var(--soft)] rounded-xl outline-none focus:ring-1 focus:ring-[var(--primary)]"
            />
            <button type="submit" className="absolute right-2 top-2 w-8 h-8 flex items-center justify-center bg-[var(--ink)] text-[var(--bg)] rounded-lg"><Plus className="w-4 h-4"/></button>
          </form>
          <div className="flex flex-wrap gap-2">
            {person.preferences.map(p => (
              <div key={p.id} className="px-3 py-1.5 bg-[var(--soft)] bg-opacity-20 border border-[var(--soft)] rounded-full text-xs flex items-center gap-2">
                <Coffee className="w-3 h-3 opacity-40" />
                {p.content}
                <button onClick={() => onUpdate({ preferences: person.preferences.filter(pr => pr.id !== p.id) })} className="opacity-30 hover:opacity-100"><Trash2 className="w-3 h-3"/></button>
              </div>
            ))}
          </div>
        </section>

        {/* Gestures/Cards */}
        <section>
          <h3 className="text-xl mb-4 font-serif font-bold">Occasion Management</h3>
          <form onSubmit={addDate} className="grid grid-cols-1 gap-2 mb-6">
