const CalendarView: React.FC<{ people: Person[], onSelectPerson: (id: string) => void }> = ({ people, onSelectPerson }) => {
  const [viewDate, setViewDate] = useState(new Date());

  const daysInMonth = new Date(viewDate.getFullYear(), viewDate.getMonth() + 1, 0).getDate();
  const firstDay = new Date(viewDate.getFullYear(), viewDate.getMonth(), 1).getDay();

  const monthEvents = useMemo(() => {
    return people.flatMap(p => 
      p.dates.map(d => ({ ...d, personId: p.id, dObj: new Date(d.date) }))
    ).filter(e => e.dObj.getMonth() === viewDate.getMonth() && e.dObj.getFullYear() === viewDate.getFullYear());
  }, [people, viewDate]);

  return (
    <div className="kokoro-card p-6 rounded-2xl border-[var(--soft)]">
      <div className="flex items-center justify-between mb-8">
        <button onClick={() => setViewDate(new Date(viewDate.setMonth(viewDate.getMonth() - 1)))} className="p-2 rounded-lg hover:bg-[var(--bg)] transition-colors"><ChevronLeft className="w-4 h-4"/></button>
        <h3 className="font-serif text-xl font-bold">{viewDate.toLocaleDateString(undefined, { month: 'long', year: 'numeric' })}</h3>
        <button onClick={() => setViewDate(new Date(viewDate.setMonth(viewDate.getMonth() + 1)))} className="p-2 rounded-lg hover:bg-[var(--bg)] transition-colors"><ChevronRight className="w-4 h-4"/></button>
      </div>
      <div className="grid grid-cols-7 gap-1">
        {['Sun','Mon','Tue','Wed','Thu','Fri','Sat'].map(d => <div key={d} className="text-center text-[10px] font-bold opacity-30 py-2">{d}</div>)}
        {Array.from({ length: firstDay }).map((_, i) => (
          <div key={`empty-${i}`} className="aspect-square opacity-10 flex items-center justify-center text-xs">
            {new Date(viewDate.getFullYear(), viewDate.getMonth(), 0).getDate() - firstDay + i + 1}
          </div>
        ))}
        {Array.from({ length: daysInMonth }).map((_, i) => {
          const day = i + 1;
          const hits = monthEvents.filter(e => e.dObj.getDate() === day);
          const isToday = new Date().toDateString() === new Date(viewDate.getFullYear(), viewDate.getMonth(), day).toDateString();
          return (
            <div 
              key={day} 
              onClick={() => hits.length > 0 && onSelectPerson(hits[0].personId)}
              className={`aspect-square relative flex flex-col items-center justify-start pt-2 rounded-lg text-sm border border-transparent transition-all ${hits.length > 0 ? 'cursor-pointer hover:border-[var(--primary)]' : ''} ${isToday ? 'border-[var(--primary)] text-[var(--primary)] font-bold' : 'opacity-70'}`}
            >
              <span>{day}</span>
              <div className="flex gap-0.5 mt-auto mb-1">
                {hits.map((_, idx) => (
                  <div key={idx} className="w-1 h-1 bg-[var(--primary)] rounded-full" />
                ))}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

const PersonProfile: React.FC<{ 
  person: Person, 
  onBack: () => void,
  onUpdate: (updates: Partial<Person>) => void,
  onDelete: (pid: string) => void
}> = ({ person, onBack, onUpdate, onDelete }) => {
  const [insights, setInsights] = useState<string | null>(null);
  const [loadingInsights, setLoadingInsights] = useState(false);
  const [newPref, setNewPref] = useState('');
  const [newDateLabel, setNewDateLabel] = useState('');
  const [newDateValue, setNewDateValue] = useState('');

  const handleUpdateField = (field: keyof Person, value: any) => {
    onUpdate({ [field]: value });
  };

  const addPref = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newPref.trim()) return;
    const pref: Preference = { id: crypto.randomUUID(), category: 'like', content: newPref, timestamp: Date.now() };
    onUpdate({ preferences: [...person.preferences, pref] });
    setNewPref('');
  };

  const addDate = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newDateLabel || !newDateValue) return;
    const date: ImportantDate = { id: crypto.randomUUID(), label: newDateLabel, date: newDateValue, recurring: true, status: 'PLANNED', leadDays: 7 };
    onUpdate({ dates: [...person.dates, date] });
    setNewDateLabel('');
    setNewDateValue('');
  };

  const fetchInsights = async () => {
    setLoadingInsights(true);
    const res = await getRelationshipAdvice(person);
    setInsights(res);
    setLoadingInsights(false);
  };

  const handleQuickOccasion = (occ: string) => {
    setNewDateLabel(occ);
    if (occ === 'Christmas') setNewDateValue(getNextOccurrence(11, 25));
    if (occ === 'Valentine\'s Day') setNewDateValue(getNextOccurrence(1, 14));
  };

  return (
    <div className="py-4 animate-in fade-in slide-in-from-right-4 duration-500">
      <div className="flex justify-between items-center mb-6">
        <button onClick={onBack} className="flex items-center gap-2 opacity-50 hover:opacity-100 transition-colors">
          <ArrowLeft className="w-4 h-4" />
          <span className="text-sm">Back Home</span>
        </button>
        <button onClick={() => handleUpdateField('pinned', !person.pinned)} className="p-2 transition-transform active:scale-125">
          <Star className={`w-5 h-5 ${person.pinned ? 'text-[var(--primary)] fill-[var(--primary)]' : 'opacity-20'}`} />
        </button>
      </div>

      <div className="flex items-center gap-6 mb-12">
        <img src={person.avatar} className="w-24 h-24 rounded-full border-4 border-[var(--card)] shadow-lg" />
        <div>
          <h2 className="text-4xl font-serif mb-1">{person.name}</h2>
          <p className="opacity-50 flex items-center gap-2">
            <Sparkles className="w-4 h-4 text-[var(--primary)]" />
            {person.relationship}
          </p>
        </div>
      </div>

      <div className="space-y-12">
        {/* Contact Info */}
        <section className="kokoro-card p-6 rounded-2xl space-y-4 border-[var(--soft)]">
          <h3 className="text-[10px] uppercase font-bold opacity-30 tracking-widest mb-2">Nuances & Contact</h3>
          <div className="grid grid-cols-1 gap-2">
            <div className="flex items-center gap-3 p-3 bg-[var(--bg)] rounded-xl border border-[var(--soft)]">
              <Phone className="w-4 h-4 opacity-40" />
              <input 
                placeholder="Phone number" 
                value={person.phone || ''} 
                onChange={(e) => handleUpdateField('phone', e.target.value)}
                className="bg-transparent text-sm w-full outline-none"
              />
            </div>
            <div className="flex items-center gap-3 p-3 bg-[var(--bg)] rounded-xl border border-[var(--soft)]">
              <Mail className="w-4 h-4 opacity-40" />
              <input 
                placeholder="Email address" 
                value={person.email || ''} 
                onChange={(e) => handleUpdateField('email', e.target.value)}
                className="bg-transparent text-sm w-full outline-none"
              />
            </div>
            <div className="flex items-center gap-3 p-3 bg-[var(--bg)] rounded-xl border border-[var(--soft)]">
              <MapPin className="w-4 h-4 opacity-40" />
              <input 
                placeholder="Physical address" 
                value={person.address || ''} 
                onChange={(e) => handleUpdateField('address', e.target.value)}
                className="bg-transparent text-sm w-full outline-none"
              />
            </div>
          </div>
        </section>

        {/* Nuances/Preferences */}
        <section>
          <h3 className="text-xl mb-4 font-serif font-bold">Deep Nuances</h3>
          <form onSubmit={addPref} className="mb-4 relative">
            <input 
              value={newPref}
              onChange={(e) => setNewPref(e.target.value)}
              placeholder="Add likes, dislikes, or rituals..."
              className="w-full pl-4 pr-12 py-3 bg-[var(--card)] border border-[var(--soft)] rounded-xl outline-none focus:ring-1 focus:ring-[var(--primary)]"
            />
            <button type="submit" className="absolute right-2 top-2 w-8 h-8 flex items-center justify-center bg-[var(--ink)] text-[var(--bg)] rounded-lg"><Plus className="w-4 h-4"/></button>
          </form>
          <div className="flex flex-wrap gap-2">
            {person.preferences.map(p => (
              <div key={p.id} className="px-3 py-1.5 bg-[var(--soft)] bg-opacity-20 border border-[var(--soft)] rounded-full text-xs flex items-center gap-2">
                <Coffee className="w-3 h-3 opacity-40" />
                {p.content}
                <button onClick={() => onUpdate({ preferences: person.preferences.filter(pr => pr.id !== p.id) })} className="opacity-30 hover:opacity-100"><Trash2 className="w-3 h-3"/></button>
              </div>
            ))}
          </div>
        </section>

        {/* Gestures/Cards */}
        <section>
          <h3 className="text-xl mb-4 font-serif font-bold">Occasion Management</h3>

          <div className="flex flex-wrap gap-2 mb-4">
            {TYPICAL_OCCASIONS.map(occ => (
              <button
                key={occ}
                type="button"
                onClick={() => handleQuickOccasion(occ)}
                className="px-3 py-1 bg-[var(--soft)] bg-opacity-20 border border-[var(--soft)] rounded-full text-[9px] uppercase tracking-wider font-bold hover:bg-[var(--primary)] hover:text-white transition-all"
              >
                + {occ}
              </button>
            ))}
          </div>

          <form onSubmit={addDate} className="grid grid-cols-1 gap-2 mb-6">
            <div className="flex gap-2">
              <input required placeholder="Occasion Type" value={newDateLabel} onChange={e => setNewDateLabel(e.target.value)} className="flex-1 px-4 py-3 bg-[var(--card)] border border-[var(--soft)] rounded-xl outline-none" />
              <input required type="date" value={newDateValue} onChange={e => setNewDateValue(e.target.value)} className="px-4 py-3 bg-[var(--card)] border border(--soft) rounded-xl outline-none" />
            </div>
            <button type="submit" className="w-full py-3 bg-[var(--ink)] text-[var(--bg)] rounded-xl font-medium shadow-md">Schedule New Card</button>
          </form>
          <div className="space-y-3">
            {person.dates.map(d => (
              <div key={d.id} className="kokoro-card p-5 rounded-2xl flex justify-between items-center border-[var(--soft)]">
                <div>
                  <p className="font-bold text-sm mb-0.5">{d.label}</p>
                  <p className="text-[10px] uppercase tracking-widest opacity-40">{new Date(d.date).toLocaleDateString(undefined, { month: 'long', day: 'numeric', year: 'numeric' })}</p>
                </div>
                <div className="flex flex-col items-end gap-2">
                  <select 
                    value={d.status}
                    onChange={(e) => {
                      const newDates = person.dates.map(date => date.id === d.id ? { ...date, status: e.target.value as CardStatus } : date);
                      onUpdate({ dates: newDates });
                    }}
                    className={`status-chip status-${d.status} bg-opacity-20 border-none outline-none cursor-pointer text-[10px]`}
                  >
                    <option value="PLANNED">Planned</option>
                    <option value="ORDERED">Ordered</option>
                    <option value="SENT">Sent</option>
                  </select>
                  <button onClick={() => onUpdate({ dates: person.dates.filter(da => da.id !== d.id) })} className="p-1 opacity-20 hover:opacity-100 hover:text-red-500 transition-all"><Trash2 className="w-3.5 h-3.5"/></button>
                </div>
              </div>
            ))}
          </div>
        </section>

        {/* AI Suggestions */}
        <section className="bg-[var(--primary)] bg-opacity-10 rounded-2xl p-6 border border-[var(--primary)] border-opacity-20 shadow-sm">
          <div className="flex items-center gap-2 mb-4">
            <Sparkles className="w-5 h-5 text-[var(--primary)]" />
            <h3 className="text-lg font-serif font-bold text-[var(--ink)]">Thoughtful Spark</h3>
          </div>
          {insights ? (
            <div className="bg-[var(--card)] p-5 rounded-xl mb-6 text-sm opacity-80 whitespace-pre-wrap leading-relaxed shadow-sm italic border border-[var(--soft)]">
              {insights}
            </div>
          ) : (
            <p className="text-sm opacity-50 mb-6 font-light italic">Reflect on their nuances to discover meaningful ways to show up for them.</p>
          )}
          <button 
            disabled={loadingInsights || person.preferences.length === 0}
            onClick={fetchInsights}
            className="w-full py-4 bg-[var(--card)] border border-[var(--primary)] text-[var(--primary)] rounded-xl font-bold text-xs uppercase tracking-widest hover:bg-[var(--primary)] hover:text-white transition-all disabled:opacity-30 shadow-sm"
          >
            {loadingInsights ? 'Meditating...' : 'Get Gestures'}
          </button>
        </section>

        <button 
          onClick={() => onDelete(person.id)} 
          className="w-full py-6 text-[10px] font-bold uppercase tracking-[0.2em] text-red-400 opacity-40 hover:opacity-100 transition-opacity flex items-center justify-center gap-3 mt-12"
        >
          <Trash2 className="w-4 h-4" />
          Purge Profile
        </button>
      </div>
    </div>
  );
};

export default App;

import React, { useState, useEffect, useMemo, useRef } from 'react';
import { Person, Preference, ImportantDate, CardStatus } from './types';
import { 
  Heart, Users, Calendar, Plus, ChevronRight, ArrowLeft, Trash2, 
  Gift, Coffee, Sparkles, Info, Settings, Star, ChevronLeft,
  Phone, Mail, MapPin, Bell, User
} from 'lucide-react';
import { getRelationshipAdvice } from './services/geminiService';

const THEMES = ['earthy', 'pastel', 'dark', 'bay'];

const TYPICAL_OCCASIONS = [
  'Birthday', 'Anniversary', 'Valentine\'s Day', 'Christmas', 
  'Mother\'s Day', 'Father\'s Day', 'Graduation', 'Milestone', 'Housewarming'
];

const getNextOccurrence = (month: number, day: number) => {
  const now = new Date();
  const year = now.getFullYear();
  let date = new Date(year, month, day);
  if (date < now) {
    date = new Date(year + 1, month, day);
  }
  return date.toISOString().split('T')[0];
};

const App: React.FC = () => {
  const [view, setView] = useState<'home' | 'rolodex' | 'settings' | 'manifesto'>('home');
  const [theme, setTheme] = useState(() => localStorage.getItem('kokoro_theme') || 'earthy');
  const [people, setPeople] = useState<Person[]>(() => {
    const saved = localStorage.getItem('kokoro_people');
    return saved ? JSON.parse(saved) : [];
  });
  const [selectedPersonId, setSelectedPersonId] = useState<string | null>(null);
  const [showAddModal, setShowAddModal] = useState(false);

  useEffect(() => {
    localStorage.setItem('kokoro_people', JSON.stringify(people));
  }, [people]);

  useEffect(() => {
    document.body.className = `theme-${theme}`;
    localStorage.setItem('kokoro_theme', theme);
  }, [theme]);

  const pinnedPeople = useMemo(() => people.filter(p => p.pinned), [people]);

  const sortedPeople = useMemo(() => {
    return [...people].sort((a, b) => a.name.localeCompare(b.name));
  }, [people]);

  const allEvents = useMemo(() => {
    return people.flatMap(p => 
      p.dates.map(d => ({ ...d, personName: p.name, personId: p.id }))
    ).sort((a, b) => new Date(a.date).getTime() - new Date(b.date).getTime());
  }, [people]);

  const upcomingHero = useMemo(() => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    return allEvents.find(e => new Date(e.date) >= today);
  }, [allEvents]);

  const addPerson = (name: string, relationship: string) => {
    const newPerson: Person = {
      id: crypto.randomUUID(),
      name,
      relationship,
      pinned: false,
      preferences: [],
      dates: [],
      notes: '',
      avatar: `https://picsum.photos/seed/${name}/200`,
    };
    setPeople([...people, newPerson]);
    setShowAddModal(false);
  };

  const updatePerson = (id: string, updates: Partial<Person>) => {
    setPeople(prev => prev.map(p => p.id === id ? { ...p, ...updates } : p));
  };

  const deletePerson = (id: string) => {
    if (confirm("Remove this person from your circle?")) {
      setPeople(people.filter(p => p.id !== id));
      setSelectedPersonId(null);
    }
  };

  const selectedPerson = people.find(p => p.id === selectedPersonId);

  // Home View Component
  const HomeView = () => (
    <div className="space-y-6 animate-in fade-in duration-500">
      {/* Top Cards */}
      <div className="grid grid-cols-2 gap-3">
        <button 
          onClick={() => setView('settings')}
          className="kokoro-card p-4 rounded-2xl flex flex-col items-start gap-1 text-left border-[var(--soft)]"
        >
          <div className="flex items-center gap-2 text-[var(--primary)] font-medium">
            <User className="w-5 h-5" />
            <span>Chris</span>
          </div>
          <span className="text-xs opacity-40">Settings & profile</span>
        </button>
        <button 
          onClick={() => setView('rolodex')}
          className="kokoro-card p-4 rounded-2xl flex flex-col items-start gap-1 text-left border-[var(--soft)]"
        >
          <div className="flex items-center gap-2 text-[var(--primary)] font-medium">
            <Users className="w-5 h-5" />
            <span>Profiles</span>
          </div>
          <span className="text-xs opacity-40">Relationship Rolodex</span>
        </button>
      </div>

      {/* Upcoming Spotlight */}
      {upcomingHero ? (
        <div 
          onClick={() => setSelectedPersonId(upcomingHero.personId)}
          className="kokoro-card p-6 rounded-2xl cursor-pointer hover:shadow-lg transition-shadow border-[var(--soft)]"
        >
          <h3 className="text-xl font-serif font-bold mb-1">
            {upcomingHero.personName} • {upcomingHero.label}
          </h3>
          <p className="text-sm opacity-50 mb-4">
            On {new Date(upcomingHero.date).toLocaleDateString(undefined, { month: 'short', day: 'numeric' })} • remind {upcomingHero.leadDays}d before
          </p>
          <div className={`status-chip status-${upcomingHero.status}`}>
            {upcomingHero.status}
          </div>
        </div>
      ) : (
        <div className="kokoro-card p-6 rounded-2xl text-center border-dashed border-2 border-[var(--soft)]">
          <p className="opacity-40 text-sm">No upcoming occasions scheduled.</p>
        </div>
      )}

      {/* Inline Calendar */}
      <CalendarView people={people} onSelectPerson={setSelectedPersonId} />

      {/* Ethos Link */}
      <button 
        onClick={() => setView('manifesto')}
        className="w-full py-4 text-xs uppercase tracking-widest opacity-30 hover:opacity-100 flex items-center justify-center gap-2 transition-opacity"
      >
        <Info className="w-3 h-3" />
        View Manifesto
      </button>
    </div>
  );

  return (
    <div className="min-h-screen max-w-2xl mx-auto flex flex-col relative pb-32 pt-12 px-4 font-sans text-[var(--ink)]">

      {/* Main Container */}
      <main className="flex-1">
        {selectedPersonId ? (
          <PersonProfile 
            person={selectedPerson!} 
            onBack={() => setSelectedPersonId(null)} 
            onUpdate={(updates) => updatePerson(selectedPerson!.id, updates)}
            onDelete={deletePerson}
          />
        ) : (
          <>
            {view === 'home' && <HomeView />}
            {view === 'rolodex' && (
              <div className="animate-in slide-in-from-bottom-4 duration-500">
                <button onClick={() => setView('home')} className="mb-6 flex items-center gap-2 opacity-50 hover:opacity-100">
                  <ArrowLeft className="w-4 h-4" /> <span>Back Home</span>
                </button>
                <div className="flex justify-between items-center mb-8">
                  <h2 className="text-3xl font-serif">Relationship Rolodex</h2>
                  <button onClick={() => setShowAddModal(true)} className="p-2 bg-[var(--ink)] text-[var(--card)] rounded-full">
                    <Plus className="w-5 h-5" />
                  </button>
                </div>
                <div className="grid gap-3">
                  {sortedPeople.map(p => (
                    <div key={p.id} onClick={() => setSelectedPersonId(p.id)} className="kokoro-card p-4 rounded-xl flex items-center gap-4 cursor-pointer hover:border-[var(--primary)] transition-all">
                      <img src={p.avatar} className="w-12 h-12 rounded-full border border-[var(--soft)]" />
                      <div>
                        <div className="flex items-center gap-2">
                          <p className="font-medium">{p.name}</p>
                          {p.pinned && <Star className="w-3 h-3 text-[var(--primary)] fill-[var(--primary)]" />}
                        </div>
                        <p className="text-xs opacity-40">{p.relationship}</p>
                      </div>
                      <ChevronRight className="ml-auto w-4 h-4 opacity-20" />
                    </div>
                  ))}
                </div>
              </div>
            )}
            {view === 'settings' && (
              <div className="animate-in fade-in duration-500">
                <button onClick={() => setView('home')} className="mb-6 flex items-center gap-2 opacity-50 hover:opacity-100">
                  <ArrowLeft className="w-4 h-4" /> <span>Back Home</span>
                </button>
                <h2 className="text-3xl font-serif mb-8">Settings</h2>
                <div className="space-y-8">
                  <div>
                    <label className="block text-xs uppercase font-bold opacity-30 mb-4 tracking-widest">Appearance</label>
                    <div className="grid grid-cols-2 gap-3">
                      {THEMES.map(t => (
                        <button 
                          key={t}
                          onClick={() => setTheme(t)}
                          className={`p-4 rounded-2xl border-2 transition-all capitalize ${theme === t ? 'border-[var(--primary)] bg-[var(--card)] shadow-md' : 'border-transparent bg-[var(--soft)] bg-opacity-20'}`}
                        >
                          {t}
                        </button>
                      ))}
                    </div>
                  </div>
                </div>
              </div>
            )}
            {view === 'manifesto' && (
              <div className="animate-in fade-in duration-500">
                <button onClick={() => setView('home')} className="mb-6 flex items-center gap-2 opacity-50 hover:opacity-100">
                  <ArrowLeft className="w-4 h-4" /> <span>Back Home</span>
                </button>
                <article className="prose prose-stone text-[var(--ink)]">
                  <h2 className="text-3xl font-serif mb-6">The Kokoro Ethos</h2>
                  <div className="space-y-6 opacity-80 leading-relaxed font-light italic">
                    <p>Connection is not an algorithm. It is the steady cultivation of the small, specific nuances that make us who we are.</p>
                    <p>Kokoro exists to help you hold those details sacred—private, intentional, and quiet.</p>
                  </div>
                </article>
              </div>
            )}
          </>
        )}
      </main>

      {/* Floating Dock & Settings Button */}
      {!selectedPersonId && (
        <div className="fixed bottom-8 left-0 right-0 px-6 flex items-center justify-between gap-4 pointer-events-none">
          {/* People Dock */}
          <div className="flex-1 bg-[var(--card)] bg-opacity-80 backdrop-blur-md rounded-full border border-[var(--soft)] p-2 flex items-center gap-3 overflow-x-auto shadow-xl no-scrollbar pointer-events-auto max-w-[calc(100%-64px)]">
            {pinnedPeople.length > 0 ? pinnedPeople.map(p => (
              <div 
                key={p.id} 
                onClick={() => setSelectedPersonId(p.id)}
                className="flex items-center gap-2 px-3 py-1.5 rounded-full border border-[var(--soft)] bg-[var(--bg)] cursor-pointer hover:bg-[var(--soft)] transition-colors whitespace-nowrap min-w-max"
              >
                <div className="w-8 h-8 rounded-full bg-[var(--accent)] flex items-center justify-center text-xs font-bold uppercase overflow-hidden">
                  {p.avatar ? <img src={p.avatar} alt="" className="w-full h-full object-cover" /> : p.name[0]}
                </div>
                <span className="text-sm font-medium">{p.name}</span>
              </div>
            )) : (
              <p className="text-[10px] uppercase tracking-widest opacity-30 px-4 py-2">Pin profiles to see them here</p>
            )}
          </div>

          {/* Settings Floating Button */}
          <button 
            onClick={() => setView('settings')}
            className="w-12 h-12 bg-[var(--soft)] text-[var(--ink)] rounded-2xl flex items-center justify-center shadow-lg border border-[var(--soft)] pointer-events-auto hover:rotate-45 transition-transform"
          >
            <Settings className="w-6 h-6 opacity-60" />
          </button>
        </div>
      )}

      {/* Add Person Modal */}
      {showAddModal && (
        <div className="fixed inset-0 bg-black bg-opacity-40 backdrop-blur-sm flex items-center justify-center p-6 z-50">
          <div className="bg-[var(--card)] text-[var(--ink)] rounded-3xl w-full max-w-md p-8 animate-in zoom-in-95 duration-200 shadow-2xl">
            <h2 className="text-2xl font-serif mb-6">New Companion</h2>
            <form onSubmit={(e) => {
              e.preventDefault();
              const formData = new FormData(e.currentTarget);
              addPerson(formData.get('name') as string, formData.get('rel') as string);
            }} className="space-y-4">
              <div>
                <label className="block text-[10px] uppercase opacity-40 font-bold mb-1 tracking-widest">Name</label>
                <input required name="name" type="text" className="w-full px-4 py-3 rounded-xl bg-[var(--bg)] border border-[var(--soft)] focus:outline-none focus:ring-2 focus:ring-[var(--primary)] transition-all" />
              </div>
              <div>
                <label className="block text-[10px] uppercase opacity-40 font-bold mb-1 tracking-widest">Relationship</label>
                <input required name="rel" type="text" className="w-full px-4 py-3 rounded-xl bg-[var(--bg)] border border-[var(--soft)] focus:outline-none focus:ring-2 focus:ring-[var(--primary)] transition-all" />
              </div>
              <div className="flex gap-3 pt-4">
                <button type="button" onClick={() => setShowAddModal(false)} className="flex-1 py-3 opacity-50 font-medium">Cancel</button>
                <button type="submit" className="flex-1 py-3 bg-[var(--ink)] text-[var(--bg)] rounded-xl font-medium shadow-lg">Save Profile</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

const CalendarView: React.FC<{ people: Person[], onSelectPerson: (id: string) => void }> = ({ people, onSelectPerson }) => {
  const [viewDate, setViewDate] = useState(new Date());

  const daysInMonth = new Date(viewDate.getFullYear(), viewDate.getMonth() + 1, 0).getDate();
  const firstDay = new Date(viewDate.getFullYear(), viewDate.getMonth(), 1).getDay();

  const monthEvents = useMemo(() => {
    return people.flatMap(p => 
      p.dates.map(d => ({ ...d, personId: p.id, dObj: new Date(d.date) }))
    ).filter(e => e.dObj.getMonth() === viewDate.getMonth() && e.dObj.getFullYear() === viewDate.getFullYear());
  }, [people, viewDate]);

  return (
    <div className="kokoro-card p-6 rounded-2xl border-[var(--soft)]">
      <div className="flex items-center justify-between mb-8">
        <button onClick={() => setViewDate(new Date(viewDate.setMonth(viewDate.getMonth() - 1)))} className="p-2 rounded-lg hover:bg-[var(--bg)] transition-colors"><ChevronLeft className="w-4 h-4"/></button>
        <h3 className="font-serif text-xl font-bold">{viewDate.toLocaleDateString(undefined, { month: 'long', year: 'numeric' })}</h3>
        <button onClick={() => setViewDate(new Date(viewDate.setMonth(viewDate.getMonth() + 1)))} className="p-2 rounded-lg hover:bg-[var(--bg)] transition-colors"><ChevronRight className="w-4 h-4"/></button>
      </div>
      <div className="grid grid-cols-7 gap-1">
        {['Sun','Mon','Tue','Wed','Thu','Fri','Sat'].map(d => <div key={d} className="text-center text-[10px] font-bold opacity-30 py-2">{d}</div>)}
        {Array.from({ length: firstDay }).map((_, i) => (
          <div key={`empty-${i}`} className="aspect-square opacity-10 flex items-center justify-center text-xs">
            {new Date(viewDate.getFullYear(), viewDate.getMonth(), 0).getDate() - firstDay + i + 1}
          </div>
        ))}
        {Array.from({ length: daysInMonth }).map((_, i) => {
          const day = i + 1;
          const hits = monthEvents.filter(e => e.dObj.getDate() === day);
          const isToday = new Date().toDateString() === new Date(viewDate.getFullYear(), viewDate.getMonth(), day).toDateString();
          return (
            <div 
              key={day} 
              onClick={() => hits.length > 0 && onSelectPerson(hits[0].personId)}
              className={`aspect-square relative flex flex-col items-center justify-start pt-2 rounded-lg text-sm border border-transparent transition-all ${hits.length > 0 ? 'cursor-pointer hover:border-[var(--primary)]' : ''} ${isToday ? 'border-[var(--primary)] text-[var(--primary)] font-bold' : 'opacity-70'}`}
            >
              <span>{day}</span>
              <div className="flex gap-0.5 mt-auto mb-1">
                {hits.map((_, idx) => (
                  <div key={idx} className="w-1 h-1 bg-[var(--primary)] rounded-full" />
                ))}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

const PersonProfile: React.FC<{ 
  person: Person, 
  onBack: () => void,
  onUpdate: (updates: Partial<Person>) => void,
  onDelete: (pid: string) => void
}> = ({ person, onBack, onUpdate, onDelete }) => {
  const [insights, setInsights] = useState<string | null>(null);
  const [loadingInsights, setLoadingInsights] = useState(false);
  const [newPref, setNewPref] = useState('');
  const [newDateLabel, setNewDateLabel] = useState('');
  const [newDateValue, setNewDateValue] = useState('');

  const dateInputRef = useRef<HTMLInputElement>(null);

  const handleUpdateField = (field: keyof Person, value: any) => {
    onUpdate({ [field]: value });
  };

  const addPref = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newPref.trim()) return;
    const pref: Preference = { id: crypto.randomUUID(), category: 'like', content: newPref, timestamp: Date.now() };
    onUpdate({ preferences: [...person.preferences, pref] });
    setNewPref('');
  };

  const addDate = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newDateLabel || !newDateValue) return;
    const date: ImportantDate = { id: crypto.randomUUID(), label: newDateLabel, date: newDateValue, recurring: true, status: 'PLANNED', leadDays: 7 };
    onUpdate({ dates: [...person.dates, date] });
    setNewDateLabel('');
    setNewDateValue('');
  };

  const fetchInsights = async () => {
    setLoadingInsights(true);
    const res = await getRelationshipAdvice(person);
    setInsights(res);
    setLoadingInsights(false);
  };

  const handleQuickOccasion = (occ: string) => {
    setNewDateLabel(occ);
    let autoFilled = false;
    if (occ === 'Christmas') {
      setNewDateValue(getNextOccurrence(11, 25));
      autoFilled = true;
    } else if (occ === 'Valentine\'s Day') {
      setNewDateValue(getNextOccurrence(1, 14));
      autoFilled = true;
    } else {
      // For others, clear the date if it's currently a different auto-filled holiday
      // or just leave it for user to pick. Focus the input to encourage picking.
      setNewDateValue('');
    }

    if (!autoFilled && dateInputRef.current) {
      dateInputRef.current.focus();
      // On mobile devices, focusing sometimes doesn't open the picker, 
      // but the visual focus still helps guidance.
    }
  };

  return (
    <div className="py-4 animate-in fade-in slide-in-from-right-4 duration-500">
      <div className="flex justify-between items-center mb-6">
        <button onClick={onBack} className="flex items-center gap-2 opacity-50 hover:opacity-100 transition-colors">
          <ArrowLeft className="w-4 h-4" />
          <span className="text-sm">Back Home</span>
        </button>
        <button onClick={() => handleUpdateField('pinned', !person.pinned)} className="p-2 transition-transform active:scale-125">
          <Star className={`w-5 h-5 ${person.pinned ? 'text-[var(--primary)] fill-[var(--primary)]' : 'opacity-20'}`} />
        </button>
      </div>

      <div className="flex items-center gap-6 mb-12">
        <img src={person.avatar} className="w-24 h-24 rounded-full border-4 border-[var(--card)] shadow-lg" />
        <div>
          <h2 className="text-4xl font-serif mb-1">{person.name}</h2>
          <p className="opacity-50 flex items-center gap-2">
            <Sparkles className="w-4 h-4 text-[var(--primary)]" />
            {person.relationship}
          </p>
        </div>
      </div>

      <div className="space-y-12">
        {/* Contact Info */}
        <section className="kokoro-card p-6 rounded-2xl space-y-4 border-[var(--soft)]">
          <h3 className="text-[10px] uppercase font-bold opacity-30 tracking-widest mb-2">Nuances & Contact</h3>
          <div className="grid grid-cols-1 gap-2">
            <div className="flex items-center gap-3 p-3 bg-[var(--bg)] rounded-xl border border-[var(--soft)]">
              <Phone className="w-4 h-4 opacity-40" />
              <input 
                placeholder="Phone number" 
                value={person.phone || ''} 
                onChange={(e) => handleUpdateField('phone', e.target.value)}
                className="bg-transparent text-sm w-full outline-none"
              />
            </div>
            <div className="flex items-center gap-3 p-3 bg-[var(--bg)] rounded-xl border border-[var(--soft)]">
              <Mail className="w-4 h-4 opacity-40" />
              <input 
                placeholder="Email address" 
                value={person.email || ''} 
                onChange={(e) => handleUpdateField('email', e.target.value)}
                className="bg-transparent text-sm w-full outline-none"
              />
            </div>
            <div className="flex items-center gap-3 p-3 bg-[var(--bg)] rounded-xl border border-[var(--soft)]">
              <MapPin className="w-4 h-4 opacity-40" />
              <input 
                placeholder="Physical address" 
                value={person.address || ''} 
                onChange={(e) => handleUpdateField('address', e.target.value)}
                className="bg-transparent text-sm w-full outline-none"
              />
            </div>
          </div>
        </section>

        {/* Nuances/Preferences */}
        <section>
          <h3 className="text-xl mb-4 font-serif font-bold">Deep Nuances</h3>
          <form onSubmit={addPref} className="mb-4 relative">
            <input 
              value={newPref}
              onChange={(e) => setNewPref(e.target.value)}
              placeholder="Add likes, dislikes, or rituals..."
              className="w-full pl-4 pr-12 py-3 bg-[var(--card)] border border-[var(--soft)] rounded-xl outline-none focus:ring-1 focus:ring-[var(--primary)]"
            />
            <button type="submit" className="absolute right-2 top-2 w-8 h-8 flex items-center justify-center bg-[var(--ink)] text-[var(--bg)] rounded-lg"><Plus className="w-4 h-4"/></button>
          </form>
          <div className="flex flex-wrap gap-2">
            {person.preferences.map(p => (
              <div key={p.id} className="px-3 py-1.5 bg-[var(--soft)] bg-opacity-20 border border-[var(--soft)] rounded-full text-xs flex items-center gap-2">
                <Coffee className="w-3 h-3 opacity-40" />
                {p.content}
                <button onClick={() => onUpdate({ preferences: person.preferences.filter(pr => pr.id !== p.id) })} className="opacity-30 hover:opacity-100"><Trash2 className="w-3.5 h-3.5"/></button>
              </div>
            ))}
          </div>
        </section>

        {/* Gestures/Cards */}
        <section>
          <h3 className="text-xl mb-4 font-serif font-bold">Occasion Management</h3>

          <div className="flex flex-wrap gap-2 mb-6">
            <span className="w-full text-[10px] uppercase tracking-widest opacity-30 mb-1 font-bold">Quick Suggestions</span>
            {TYPICAL_OCCASIONS.map(occ => (
              <button
                key={occ}
                type="button"
                onClick={() => handleQuickOccasion(occ)}
                className={`px-3 py-2 rounded-full text-[10px] uppercase tracking-wider font-bold transition-all border ${newDateLabel === occ ? 'bg-[var(--primary)] text-white border-[var(--primary)]' : 'bg-[var(--soft)] bg-opacity-20 border-[var(--soft)] opacity-60 hover:opacity-100'}`}
              >
                {occ}
              </button>
            ))}
          </div>

          <form onSubmit={addDate} className="kokoro-card p-6 rounded-2xl border-[var(--soft)] bg-opacity-30 space-y-4">
            <div>
              <label className="block text-[10px] uppercase font-bold opacity-30 tracking-widest mb-2">Occasion Type</label>
              <input 
                required 
                placeholder="e.g. 30th Birthday" 
                value={newDateLabel} 
                onChange={e => setNewDateLabel(e.target.value)} 
                className="w-full px-4 py-3 bg-[var(--card)] border border-[var(--soft)] rounded-xl outline-none focus:ring-1 focus:ring-[var(--primary)]" 
              />
            </div>
            <div>
              <label className="block text-[10px] uppercase font-bold opacity-30 tracking-widest mb-2">Event Date</label>
              <input 
                ref={dateInputRef}
                required 
                type="date" 
                value={newDateValue} 
                onChange={e => setNewDateValue(e.target.value)} 
                className="w-full px-4 py-3 bg-[var(--card)] border border-[var(--soft)] rounded-xl outline-none focus:ring-1 focus:ring-[var(--primary)]" 
              />
            </div>
            <button type="submit" className="w-full py-4 bg-[var(--ink)] text-[var(--bg)] rounded-xl font-bold uppercase text-[10px] tracking-[0.2em] shadow-lg hover:translate-y-[-1px] transition-transform">
              Schedule Ritual
            </button>
          </form>

          <div className="space-y-3 mt-8">
            <h3 className="text-[10px] uppercase font-bold opacity-30 tracking-widest mb-4">Upcoming Schedule</h3>
            {person.dates.length === 0 ? (
              <p className="text-sm opacity-30 italic text-center py-8">No rituals scheduled yet.</p>
            ) : person.dates.map(d => (
              <div key={d.id} className="kokoro-card p-5 rounded-2xl flex justify-between items-center border-[var(--soft)]">
                <div>
                  <p className="font-bold text-sm mb-0.5">{d.label}</p>
                  <p className="text-[10px] uppercase tracking-widest opacity-40">{new Date(d.date).toLocaleDateString(undefined, { month: 'long', day: 'numeric', year: 'numeric' })}</p>
                </div>
                <div className="flex flex-col items-end gap-2">
                  <select 
                    value={d.status}
                    onChange={(e) => {
                      const newDates = person.dates.map(date => date.id === d.id ? { ...date, status: e.target.value as CardStatus } : date);
                      onUpdate({ dates: newDates });
                    }}
                    className={`status-chip status-${d.status} bg-opacity-20 border-none outline-none cursor-pointer text-[10px]`}
                  >
                    <option value="PLANNED">Planned</option>
                    <option value="ORDERED">Ordered</option>
                    <option value="SENT">Sent</option>
                  </select>
                  <button onClick={() => onUpdate({ dates: person.dates.filter(da => da.id !== d.id) })} className="p-1 opacity-20 hover:opacity-100 hover:text-red-500 transition-all"><Trash2 className="w-3.5 h-3.5"/></button>
                </div>
              </div>
            ))}
          </div>
        </section>

        {/* AI Suggestions */}
        <section className="bg-[var(--primary)] bg-opacity-10 rounded-2xl p-6 border border-[var(--primary)] border-opacity-20 shadow-sm">
