  const handleUpdateField = (field: keyof Person, value: any) => {
    onUpdate({ [field]: value });
  };

  const addPref = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newPref.trim()) return;
    const pref: Preference = { id: crypto.randomUUID(), category: 'like', content: newPref, timestamp: Date.now() };
    onUpdate({ preferences: [...person.preferences, pref] });
    setNewPref('');
  };

  const addDate = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newDateLabel || !newDateValue) return;
    const date: ImportantDate = { id: crypto.randomUUID(), label: newDateLabel, date: newDateValue, recurring: true, status: 'PLANNED', leadDays: 7 };
    onUpdate({ dates: [...person.dates, date] });
    setNewDateLabel('');
    setNewDateValue('');
  };

  const fetchInsights = async () => {
    setLoadingInsights(true);
    const res = await getRelationshipAdvice(person);
    setInsights(res);
    setLoadingInsights(false);
  };

  const handleQuickOccasion = (occ: string) => {
    setNewDateLabel(occ);
    let autoFilled = false;
    if (occ === 'Christmas') {
      setNewDateValue(getNextOccurrence(11, 25));
      autoFilled = true;
    } else if (occ === 'Valentine\'s Day') {
      setNewDateValue(getNextOccurrence(1, 14));
      autoFilled = true;
    } else {
      setNewDateValue('');
    }

    if (!autoFilled && dateInputRef.current) {
      dateInputRef.current.focus();
    }
  };

  return (
    <div className="py-4 animate-in fade-in slide-in-from-right-4 duration-500 pb-20">
      <div className="flex justify-between items-center mb-6">
        <button onClick={onBack} className="flex items-center gap-2 opacity-50 hover:opacity-100 transition-colors">
          <ArrowLeft className="w-4 h-4" />
          <span className="text-sm">Back Home</span>
        </button>
        <button onClick={() => handleUpdateField('pinned', !person.pinned)} className="p-2 transition-transform active:scale-125">
          <Star className={`w-5 h-5 ${person.pinned ? 'text-[var(--primary)] fill-[var(--primary)]' : 'opacity-20'}`} />
        </button>
      </div>

      <div className="flex items-center gap-6 mb-12">
        <img src={person.avatar} className="w-24 h-24 rounded-full border-4 border-[var(--card)] shadow-lg" />
        <div>
          <h2 className="text-4xl font-serif mb-1">{person.name}</h2>
          <p className="opacity-50 flex items-center gap-2">
            <Sparkles className="w-4 h-4 text-[var(--primary)]" />
            {person.relationship}
          </p>
        </div>
      </div>

      <div className="space-y-12">
        {/* Contact Info */}
        <section className="kokoro-card p-6 rounded-2xl space-y-4 border-[var(--soft)]">
          <h3 className="text-[10px] uppercase font-bold opacity-30 tracking-widest mb-2">Nuances & Contact</h3>
          <div className="grid grid-cols-1 gap-2">
            <div className="flex items-center gap-3 p-3 bg-[var(--bg)] rounded-xl border border-[var(--soft)]">
              <Phone className="w-4 h-4 opacity-40" />
              <input 
                placeholder="Phone number" 
                value={person.phone || ''} 
                onChange={(e) => handleUpdateField('phone', e.target.value)}
                className="bg-transparent text-sm w-full outline-none"
              />
            </div>
            <div className="flex items-center gap-3 p-3 bg-[var(--bg)] rounded-xl border border-[var(--soft)]">
              <Mail className="w-4 h-4 opacity-40" />
              <input 
                placeholder="Email address" 
                value={person.email || ''} 
                onChange={(e) => handleUpdateField('email', e.target.value)}
                className="bg-transparent text-sm w-full outline-none"
              />
            </div>
            <div className="flex items-center gap-3 p-3 bg-[var(--bg)] rounded-xl border border-[var(--soft)]">
              <MapPin className="w-4 h-4 opacity-40" />
              <input 
                placeholder="Physical address" 
                value={person.address || ''} 
                onChange={(e) => handleUpdateField('address', e.target.value)}
                className="bg-transparent text-sm w-full outline-none"
              />
            </div>
          </div>
        </section>

        {/* Nuances/Preferences */}
        <section>
          <h3 className="text-xl mb-4 font-serif font-bold">Deep Nuances</h3>
          <form onSubmit={addPref} className="mb-4 relative">
            <input 
              value={newPref}
              onChange={(e) => setNewPref(e.target.value)}
              placeholder="Add likes, dislikes, or rituals..."
              className="w-full pl-4 pr-12 py-3 bg-[var(--card)] border border-[var(--soft)] rounded-xl outline-none focus:ring-1 focus:ring-[var(--primary)]"
            />
            <button type="submit" className="absolute right-2 top-2 w-8 h-8 flex items-center justify-center bg-[var(--ink)] text-[var(--bg)] rounded-lg"><Plus className="w-4 h-4"/></button>
          </form>
          <div className="flex flex-wrap gap-2">
            {person.preferences.map(p => (
              <div key={p.id} className="px-3 py-1.5 bg-[var(--soft)] bg-opacity-20 border border-[var(--soft)] rounded-full text-xs flex items-center gap-2">
                <Coffee className="w-3 h-3 opacity-40" />
                {p.content}
                <button onClick={() => onUpdate({ preferences: person.preferences.filter(pr => pr.id !== p.id) })} className="opacity-30 hover:opacity-100"><Trash2 className="w-3.5 h-3.5"/></button>
              </div>
            ))}
          </div>
        </section>

        {/* Gestures/Cards */}
        <section>
          <h3 className="text-xl mb-4 font-serif font-bold">Occasion Management</h3>

          <div className="flex flex-wrap gap-2 mb-6">
            <span className="w-full text-[10px] uppercase tracking-widest opacity-30 mb-1 font-bold">Quick Suggestions</span>
            {TYPICAL_OCCASIONS.map(occ => (
              <button
                key={occ}
                type="button"
                onClick={() => handleQuickOccasion(occ)}
                className={`px-3 py-2 rounded-full text-[10px] uppercase tracking-wider font-bold transition-all border ${newDateLabel === occ ? 'bg-[var(--primary)] text-white border-[var(--primary)]' : 'bg-[var(--soft)] bg-opacity-20 border-[var(--soft)] opacity-60 hover:opacity-100'}`}
              >
                {occ}
              </button>
            ))}
          </div>

          <form onSubmit={addDate} className="kokoro-card p-6 rounded-2xl border-[var(--soft)] bg-opacity-30 space-y-4">
            <div>
              <label className="block text-[10px] uppercase font-bold opacity-30 tracking-widest mb-2">Occasion Type</label>
              <input 
                required 
                placeholder="e.g. 30th Birthday" 
                value={newDateLabel} 
                onChange={e => setNewDateLabel(e.target.value)} 
                className="w-full px-4 py-3 bg-[var(--card)] border border-[var(--soft)] rounded-xl outline-none focus:ring-1 focus:ring-[var(--primary)]" 
              />
            </div>
            <div>
              <label className="block text-[10px] uppercase font-bold opacity-30 tracking-widest mb-2">Event Date</label>
              <input 
                ref={dateInputRef}
                required 
                type="date" 
                value={newDateValue} 
                onChange={e => setNewDateValue(e.target.value)} 
                className="w-full px-4 py-3 bg-[var(--card)] border border-[var(--soft)] rounded-xl outline-none focus:ring-1 focus:ring-[var(--primary)]" 
              />
            </div>
            <button type="submit" className="w-full py-4 bg-[var(--ink)] text-[var(--bg)] rounded-xl font-bold uppercase text-[10px] tracking-[0.2em] shadow-lg hover:translate-y-[-1px] transition-transform">
              Schedule Ritual
            </button>
          </form>

          <div className="space-y-3 mt-8">
            <h3 className="text-[10px] uppercase font-bold opacity-30 tracking-widest mb-4">Upcoming Schedule</h3>
            {person.dates.length === 0 ? (
              <p className="text-sm opacity-30 italic text-center py-8">No rituals scheduled yet.</p>
            ) : person.dates.map(d => (
              <div key={d.id} className="kokoro-card p-5 rounded-2xl flex justify-between items-center border-[var(--soft)]">
                <div>
                  <p className="font-bold text-sm mb-0.5">{d.label}</p>
                  <p className="text-[10px] uppercase tracking-widest opacity-40">{new Date(d.date).toLocaleDateString(undefined, { month: 'long', day: 'numeric', year: 'numeric' })}</p>
                </div>
                <div className="flex flex-col items-end gap-2">
                  <select 
                    value={d.status}
                    onChange={(e) => {
                      const newDates = person.dates.map(date => date.id === d.id ? { ...date, status: e.target.value as CardStatus } : date);
                      onUpdate({ dates: newDates });
                    }}
                    className={`status-chip status-${d.status} bg-opacity-20 border-none outline-none cursor-pointer text-[10px]`}
                  >
                    <option value="PLANNED">Planned</option>
                    <option value="ORDERED">Ordered</option>
                    <option value="SENT">Sent</option>
                  </select>
                  <button onClick={() => onUpdate({ dates: person.dates.filter(da => da.id !== d.id) })} className="p-1 opacity-20 hover:opacity-100 hover:text-red-500 transition-all"><Trash2 className="w-3.5 h-3.5"/></button>
                </div>
              </div>
            ))}
          </div>
        </section>

        {/* AI Suggestions */}
        <section className="bg-[var(--primary)] bg-opacity-10 rounded-2xl p-6 border border-[var(--primary)] border-opacity-20 shadow-sm">
          <div className="flex items-center gap-2 mb-4">
            <Sparkles className="w-5 h-5 text-[var(--primary)]" />
            <h3 className="text-lg font-serif font-bold text-[var(--ink)]">Thoughtful Spark</h3>
          </div>
          {insights ? (
            <div className="bg-[var(--card)] p-5 rounded-xl mb-6 text-sm opacity-80 whitespace-pre-wrap leading-relaxed shadow-sm italic border border-[var(--soft)]">
              {insights}
            </div>
          ) : (
            <p className="text-sm opacity-50 mb-6 font-light italic">Reflect on their nuances to discover meaningful ways to show up for them.</p>
          )}
          <button 
            disabled={loadingInsights || person.preferences.length === 0}
            onClick={fetchInsights}
            className="w-full py-4 bg-[var(--card)] border border-[var(--primary)] text-[var(--primary)] rounded-xl font-bold text-xs uppercase tracking-widest hover:bg-[var(--primary)] hover:text-white transition-all disabled:opacity-30 shadow-sm"
          >
            {loadingInsights ? 'Meditating...' : 'Get Gestures'}
          </button>
        </section>

        <button 
          onClick={() => onDelete(person.id)} 
          className="w-full py-6 text-[10px] font-bold uppercase tracking-[0.2em] text-red-400 opacity-40 hover:opacity-100 transition-opacity flex items-center justify-center gap-3 mt-12"
        >
          <Trash2 className="w-4 h-4" />
          Purge Profile
        </button>
      </div>
    </div>
  );
};

export default App;

{
  "name": "Kokoro",
  "description": "A quiet, private relationship companion designed to help you nurture the bonds that matter most through intentional journaling, preference tracking, and thoughtful reminders.",
  "requestFramePermissions": [
    "microphone"
  ]
}

import React, { useState, useEffect, useMemo, useRef } from 'react';
import { Person, Preference, ImportantDate, CardStatus } from './types';
import { 
  Heart, Users, Calendar, Plus, ChevronRight, ArrowLeft, Trash2, 
  Gift, Coffee, Sparkles, Info, Settings, Star, ChevronLeft,
  Phone, Mail, MapPin, Bell, User, Send, Bot, Loader2, Mic, MicOff
} from 'lucide-react';
import { getRelationshipAdvice, processCommand } from './services/geminiService';

const THEMES = ['earthy', 'pastel', 'dark', 'bay'];

const TYPICAL_OCCASIONS = [
  'Birthday', 'Anniversary', 'Valentine\'s Day', 'Christmas', 
  'Mother\'s Day', 'Father\'s Day', 'Graduation', 'Milestone', 'Housewarming'
];

const getNextOccurrence = (month: number, day: number) => {
  const now = new Date();
  const year = now.getFullYear();
  let date = new Date(year, month, day);
  if (date < now) {
    date = new Date(year + 1, month, day);
  }
  return date.toISOString().split('T')[0];
};

const App: React.FC = () => {
  const [view, setView] = useState<'home' | 'rolodex' | 'settings' | 'manifesto'>('home');
  const [theme, setTheme] = useState(() => localStorage.getItem('kokoro_theme') || 'earthy');
  const [people, setPeople] = useState<Person[]>(() => {
    const saved = localStorage.getItem('kokoro_people');
    return saved ? JSON.parse(saved) : [];
  });
  const [selectedPersonId, setSelectedPersonId] = useState<string | null>(null);
  const [showAddModal, setShowAddModal] = useState(false);

  // AI Chat State
  const [chatInput, setChatInput] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [chatHistory, setChatHistory] = useState<{ role: 'user' | 'bot', content: string }[]>([]);
  const chatEndRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    localStorage.setItem('kokoro_people', JSON.stringify(people));
  }, [people]);

  useEffect(() => {
    document.body.className = `theme-${theme}`;
    localStorage.setItem('kokoro_theme', theme);
  }, [theme]);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [chatHistory, isProcessing]);

  const sortedPeople = useMemo(() => {
    return [...people].sort((a, b) => a.name.localeCompare(b.name));
  }, [people]);

  const allEvents = useMemo(() => {
    return people.flatMap(p => 
      p.dates.map(d => ({ ...d, personName: p.name, personId: p.id }))
    ).sort((a, b) => new Date(a.date).getTime() - new Date(b.date).getTime());
  }, [people]);

  const upcomingEvents = useMemo(() => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    return allEvents.filter(e => new Date(e.date) >= today).slice(0, 3);
  }, [allEvents]);

  const addPerson = (name: string, relationship: string) => {
    const newPerson: Person = {
      id: crypto.randomUUID(),
      name,
      relationship,
      pinned: false,
      preferences: [],
      dates: [],
      notes: '',
      avatar: `https://picsum.photos/seed/${name}/200`,
    };
    setPeople([...people, newPerson]);
    return newPerson;
  };

  const updatePerson = (id: string, updates: Partial<Person>) => {
    setPeople(prev => prev.map(p => p.id === id ? { ...p, ...updates } : p));
  };

  const deletePerson = (id: string) => {
    if (confirm("Remove this person from your circle?")) {
      setPeople(people.filter(p => p.id !== id));
      setSelectedPersonId(null);
    }
  };

  const handleAIChat = async (e?: React.FormEvent, directInput?: string) => {
    if (e) e.preventDefault();
    const query = directInput || chatInput;
    if (!query.trim() || isProcessing) return;

    setChatInput('');
    setChatHistory(prev => [...prev, { role: 'user', content: query }]);
    setIsProcessing(true);

    try {
      const names = people.map(p => p.name);
      const result = await processCommand(query, names);

      setChatHistory(prev => [...prev, { role: 'bot', content: result.message }]);

      if (result.command) {
        const { action, data } = result.command;
        if (action === 'ADD_PERSON' && data.name) {
          addPerson(data.name, data.relationship || 'Friend');
        } else if (action === 'ADD_DATE' && data.name && data.label && data.date) {
          const person = people.find(p => p.name.toLowerCase() === data.name.toLowerCase());
          if (person) {
            const newDate: ImportantDate = {
              id: crypto.randomUUID(),
              label: data.label,
              date: data.date,
              recurring: true,
              status: 'PLANNED',
              leadDays: 7
            };
            updatePerson(person.id, { dates: [...person.dates, newDate] });
          }
        } else if (action === 'ADD_PREFERENCE' && data.name && data.content) {
          const person = people.find(p => p.name.toLowerCase() === data.name.toLowerCase());
          if (person) {
            const newPref: Preference = {
              id: crypto.randomUUID(),
              category: 'fact',
              content: data.content,
              timestamp: Date.now()
            };
            updatePerson(person.id, { preferences: [...person.preferences, newPref] });
          }
        }
      }
    } catch (err) {
      setChatHistory(prev => [...prev, { role: 'bot', content: "Something went wrong. Let's try again." }]);
    } finally {
      setIsProcessing(false);
    }
  };

  const toggleListening = () => {
    const SpeechRecognition = (window as any).SpeechRecognition || (window as any).webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert("Speech recognition is not supported in your browser.");
      return;
    }

    if (isListening) {
      setIsListening(false);
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = 'en-US';
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;

    recognition.onstart = () => {
      setIsListening(true);
    };

    recognition.onresult = (event: any) => {
      const transcript = event.results[0][0].transcript;
      setChatInput(transcript);
      // Optional: auto-send
      // handleAIChat(undefined, transcript);
    };

    recognition.onerror = (event: any) => {
      console.error("Speech recognition error:", event.error);
      setIsListening(false);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognition.start();
  };

  const selectedPerson = people.find(p => p.id === selectedPersonId);

  // Home View Component
  const HomeView = () => (
    <div className="space-y-6 animate-in fade-in duration-500 pb-12">
      {/* AI Bot Top Section */}
      <section className="kokoro-card rounded-2xl border-[var(--soft)] p-1 overflow-hidden">
        <div className="bg-[var(--soft)] bg-opacity-10 p-4 border-b border-[var(--soft)] flex items-center justify-between">
          <div className="flex items-center gap-2">
            <Bot className="w-5 h-5 text-[var(--primary)]" />
            <span className="text-xs font-bold uppercase tracking-widest opacity-60">Kokoro Assistant</span>
          </div>
          {isListening && <div className="flex items-center gap-1"><div className="w-2 h-2 bg-red-500 rounded-full animate-pulse"></div><span className="text-[10px] uppercase font-bold opacity-40">Listening</span></div>}
        </div>
        <div className="max-h-48 overflow-y-auto p-4 space-y-3 custom-scrollbar">
          {chatHistory.length === 0 ? (
            <div className="text-center py-6">
              <Sparkles className="w-6 h-6 mx-auto mb-2 opacity-20 text-[var(--primary)]" />
              <p className="text-sm opacity-40 italic">"Add Mom's birthday on Dec 20"<br/>or "Chris hates cilantro"</p>
            </div>
          ) : (
            chatHistory.map((chat, i) => (
              <div key={i} className={`flex ${chat.role === 'user' ? 'justify-end' : 'justify-start'}`}>
                <div className={`max-w-[85%] rounded-2xl px-4 py-2 text-sm shadow-sm ${chat.role === 'user' ? 'bg-[var(--ink)] text-[var(--bg)] rounded-tr-none' : 'bg-[var(--soft)] bg-opacity-20 text-[var(--ink)] rounded-tl-none border border-[var(--soft)] border-opacity-30'}`}>
                  {chat.content}
                </div>
              </div>
            ))
          )}
          {isProcessing && (
            <div className="flex justify-start">
              <div className="bg-[var(--soft)] bg-opacity-20 rounded-2xl px-4 py-2 text-sm animate-pulse border border-[var(--soft)] border-opacity-30">
                <Loader2 className="w-4 h-4 animate-spin" />
              </div>
            </div>
          )}
          <div ref={chatEndRef} />
        </div>
        <form onSubmit={handleAIChat} className="p-2 bg-[var(--card)] flex gap-2 items-center">
          <button 
            type="button" 
            onClick={toggleListening}
            className={`p-3 rounded-xl transition-all ${isListening ? 'bg-red-500 text-white animate-pulse' : 'bg-[var(--soft)] bg-opacity-20 text-[var(--ink)] opacity-60 hover:opacity-100'}`}
          >
            {isListening ? <MicOff className="w-5 h-5" /> : <Mic className="w-5 h-5" />}
          </button>
          <input 
            value={chatInput}
            onChange={(e) => setChatInput(e.target.value)}
            placeholder={isListening ? "Listening..." : "Tell Kokoro anything..."}
            className="flex-1 bg-transparent text-sm px-2 py-2 outline-none border-b border-transparent focus:border-[var(--primary)] transition-all"
          />
          <button type="submit" disabled={isProcessing || !chatInput.trim()} className="p-3 bg-[var(--ink)] text-[var(--bg)] rounded-xl hover:opacity-90 disabled:opacity-30 transition-all shadow-md active:scale-95">
            <Send className="w-5 h-5" />
          </button>
        </form>
      </section>

      {/* Top Cards Grid */}
      <div className="grid grid-cols-2 gap-3">
        <button 
          onClick={() => setView('settings')}
          className="kokoro-card p-4 rounded-2xl flex flex-col items-start gap-1 text-left border-[var(--soft)] transition-all hover:translate-y-[-2px] active:scale-95"
        >
          <div className="flex items-center gap-2 text-[var(--primary)] font-medium">
            <User className="w-5 h-5" />
            <span>Chris</span>
          </div>
          <span className="text-[10px] uppercase tracking-wider opacity-40">Settings & profile</span>
        </button>
        <button 
          onClick={() => setView('rolodex')}
          className="kokoro-card p-4 rounded-2xl flex flex-col items-start gap-1 text-left border-[var(--soft)] transition-all hover:translate-y-[-2px] active:scale-95"
        >
          <div className="flex items-center gap-2 text-[var(--primary)] font-medium">
            <Users className="w-5 h-5" />
            <span>Profiles</span>
          </div>
          <span className="text-[10px] uppercase tracking-wider opacity-40">Relationship Rolodex</span>
        </button>
      </div>

      {/* Inline Calendar */}
      <CalendarView people={people} onSelectPerson={setSelectedPersonId} />

      {/* Upcoming Events Bottom Section */}
      <section className="space-y-3">
        <div className="flex items-center justify-between px-2">
          <h3 className="text-[10px] uppercase font-bold opacity-30 tracking-[0.2em]">Upcoming Occasions</h3>
          {upcomingEvents.length > 0 && <span className="text-[9px] opacity-20 uppercase font-bold">{upcomingEvents.length} Events</span>}
        </div>
        {upcomingEvents.length > 0 ? (
          <div className="grid gap-3">
            {upcomingEvents.map(e => (
              <div 
                key={e.id}
                onClick={() => setSelectedPersonId(e.personId)}
                className="kokoro-card p-5 rounded-2xl cursor-pointer hover:shadow-lg transition-all border-[var(--soft)] flex items-center justify-between group"
              >
                <div>
                  <h4 className="font-serif font-bold text-lg group-hover:text-[var(--primary)] transition-colors">{e.personName}</h4>
                  <p className="text-xs opacity-50">{e.label} • {new Date(e.date).toLocaleDateString(undefined, { month: 'short', day: 'numeric' })}</p>
                </div>
                <div className={`status-chip status-${e.status} shadow-sm`}>
                  {e.status}
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="kokoro-card p-12 rounded-2xl text-center border-dashed border-2 border-[var(--soft)] bg-transparent opacity-40">
            <p className="text-sm">No upcoming occasions scheduled.</p>
          </div>
        )}
      </section>

      <button 
        onClick={() => setView('manifesto')}
        className="w-full py-8 text-[10px] uppercase tracking-[0.3em] opacity-30 hover:opacity-100 flex items-center justify-center gap-2 transition-all"
      >
        <Info className="w-3 h-3" />
        View Manifesto
      </button>
    </div>
  );

  return (
    <div className="min-h-screen max-w-2xl mx-auto flex flex-col relative pb-8 pt-12 px-4 font-sans text-[var(--ink)]">

      {/* Main Container */}
      <main className="flex-1">
        {selectedPersonId ? (
          <PersonProfile 
            person={selectedPerson!} 
            onBack={() => setSelectedPersonId(null)} 
            onUpdate={(updates) => updatePerson(selectedPerson!.id, updates)}
            onDelete={deletePerson}
          />
        ) : (
          <>
            {view === 'home' && <HomeView />}
            {view === 'rolodex' && (
              <div className="animate-in slide-in-from-bottom-4 duration-500 pb-20">
                <button onClick={() => setView('home')} className="mb-6 flex items-center gap-2 opacity-50 hover:opacity-100 group transition-all">
                  <ArrowLeft className="w-4 h-4 group-hover:translate-x-[-2px] transition-transform" /> <span>Back Home</span>
                </button>
                <div className="flex justify-between items-center mb-8">
