import { GoogleGenAI, Type } from "@google/genai";
import { Person } from "../types";

const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

export const getRelationshipAdvice = async (person: Person) => {
  const preferencesText = person.preferences.map(p => `- ${p.category}: ${p.content}`).join('\n');
  const datesText = person.dates.map(d => `- ${d.label}: ${d.date}`).join('\n');

  const prompt = `
    Based ONLY on the following information about ${person.name}, who is my ${person.relationship}, provide 3 thoughtful, small gestures or gift ideas.

    Preferences:
    ${preferencesText}

    Important Dates:
    ${datesText}

    Context:
    ${person.notes}

    Be minimalist, warm, and avoid generic suggestions. Focus on the specific details provided. 
    Format as a brief list. Do not use markdown headers.
  `;

  try {
    const response = await ai.models.generateContent({
      model: 'gemini-3-flash-preview',
      contents: prompt,
    });
    return response.text;
  } catch (error) {
    console.error("Gemini Error:", error);
    return "I couldn't generate insights right now. Please try again later.";
  }
};

export const processCommand = async (userInput: string, existingPeople: string[]) => {
  const prompt = `
    You are the Kokoro AI Assistant. You help users manage their relationship circle.

    Users might want to:
    1. ADD_PERSON: Add a new person (e.g., "Add my sister Sarah")
    2. ADD_DATE: Add a date/event (e.g., "Sarah's birthday is June 12")
    3. ADD_PREFERENCE: Add a nuance/fact (e.g., "Sarah loves dark chocolate")
    4. UPDATE_PERSON: Update contact info or details (e.g., "Sarah's phone number is 555-0199", "Sarah's address is 123 Maple St", "Change Sarah's relationship to Best Friend")
    5. NONE: Just chat or ask for advice.

    Existing people in the system: ${existingPeople.join(', ')}

    Analyze the user's input: "${userInput}"

    Return a response that includes a friendly message and a structured command. 
    For UPDATE_PERSON, ensure the 'name' matches one of the existing people if possible.
  `;

  try {
    const response = await ai.models.generateContent({
      model: 'gemini-3-flash-preview',
      contents: prompt,
      config: {
        responseMimeType: "application/json",
        responseSchema: {
          type: Type.OBJECT,
          properties: {
            message: { type: Type.STRING, description: "A friendly response acknowledging the action." },
            command: {
              type: Type.OBJECT,
              properties: {
                action: { 
                  type: Type.STRING, 
                  description: "One of: ADD_PERSON, ADD_DATE, ADD_PREFERENCE, UPDATE_PERSON, NONE" 
                },
                data: {
                  type: Type.OBJECT,
                  properties: {
                    name: { type: Type.STRING },
                    relationship: { type: Type.STRING },
                    label: { type: Type.STRING },
                    date: { type: Type.STRING, description: "ISO date string or YYYY-MM-DD" },
                    content: { type: Type.STRING },
                    phone: { type: Type.STRING },
                    email: { type: Type.STRING },
                    address: { type: Type.STRING },
                    notes: { type: Type.STRING },
                    nickname: { type: Type.STRING }
                  }
                }
              }
            }
          },
          required: ["message"]
        }
      }
    });

    return JSON.parse(response.text);
  } catch (error) {
    console.error("Command processing error:", error);
    return { message: "I'm having trouble processing that right now.", command: { action: "NONE" } };
  }
};

