              <div key={e.id} onClick={() => setSelectedPersonId(e.personId)} className="kokoro-card p-5 rounded-2xl cursor-pointer hover:shadow-lg transition-all border-[var(--soft)] flex items-center justify-between group">
                <div>
                  <h4 className="font-serif font-bold text-lg group-hover:text-[var(--primary)] transition-colors">{e.personName}</h4>
                  <p className="text-xs opacity-50">{e.label} • {new Date(e.date).toLocaleDateString(undefined, { month: 'short', day: 'numeric' })}</p>
                </div>
                <div className={`status-chip status-${e.status} shadow-sm`}>{e.status}</div>
              </div>
            ))}
          </div>
        ) : (
          <div className="kokoro-card p-12 rounded-2xl text-center border-dashed border-2 border-[var(--soft)] bg-transparent opacity-40">
            <p className="text-sm">No rituals scheduled.</p>
          </div>
        )}
      </section>
    </div>
  );
};

const App: React.FC = () => {
  const [view, setView] = useState<'home' | 'rolodex' | 'settings' | 'manifesto'>('home');
  const [theme, setTheme] = useState(() => localStorage.getItem('kokoro_theme') || 'earthy');
  const [people, setPeople] = useState<Person[]>(() => {
    const saved = localStorage.getItem('kokoro_people');
    return saved ? JSON.parse(saved) : [];
  });
  const [selectedPersonId, setSelectedPersonId] = useState<string | null>(null);
  const [showAddModal, setShowAddModal] = useState(false);

  const [chatInput, setChatInput] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [chatHistory, setChatHistory] = useState<{ role: 'user' | 'bot', content: string }[]>([]);

  useEffect(() => {
    localStorage.setItem('kokoro_people', JSON.stringify(people));
  }, [people]);

  useEffect(() => {
    document.body.className = `theme-${theme}`;
    localStorage.setItem('kokoro_theme', theme);
  }, [theme]);

  const sortedPeople = useMemo(() => {
    return [...people].sort((a, b) => a.name.localeCompare(b.name));
  }, [people]);

  const allEvents = useMemo(() => {
    return people.flatMap(p => 
      p.dates.map(d => ({ ...d, personName: p.name, personId: p.id }))
    ).sort((a, b) => new Date(a.date).getTime() - new Date(b.date).getTime());
  }, [people]);

  const upcomingEvents = useMemo(() => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    return allEvents.filter(e => new Date(e.date) >= today).slice(0, 3);
  }, [allEvents]);

  const addPerson = (name: string, relationship: string) => {
    const newPerson: Person = {
      id: crypto.randomUUID(),
      name,
      relationship,
      pinned: false,
      preferences: [],
      dates: [],
      notes: '',
      avatar: `https://picsum.photos/seed/${name}/200`,
    };
    setPeople(prev => [...prev, newPerson]);
    return newPerson;
  };

  const updatePerson = (id: string, updates: Partial<Person>) => {
    setPeople(prev => prev.map(p => p.id === id ? { ...p, ...updates } : p));
  };

  const deletePerson = (id: string) => {
    if (confirm("Remove this person from your circle?")) {
      setPeople(people.filter(p => p.id !== id));
      setSelectedPersonId(null);
    }
  };

  const handleAIChat = async (e?: React.FormEvent, directInput?: string) => {
    if (e) e.preventDefault();
    const query = directInput || chatInput;
    if (!query.trim() || isProcessing) return;

    setChatInput('');
    setChatHistory(prev => [...prev, { role: 'user', content: query }]);
    setIsProcessing(true);

    try {
      const names = people.map(p => p.name);
      const result = await processCommand(query, names);

      setChatHistory(prev => [...prev, { role: 'bot', content: result.message }]);

      if (result.command) {
        const { action, data } = result.command;
        if (action === 'ADD_PERSON' && data.name) {
          addPerson(data.name, data.relationship || 'Friend');
        } else if (action === 'ADD_DATE' && data.name && data.label && data.date) {
          const person = people.find(p => p.name.toLowerCase() === data.name.toLowerCase());
          if (person) {
            updatePerson(person.id, { dates: [...person.dates, { id: crypto.randomUUID(), label: data.label, date: data.date, recurring: true, status: 'PLANNED', leadDays: 7 }] });
          }
        } else if (action === 'ADD_PREFERENCE' && data.name && data.content) {
          const person = people.find(p => p.name.toLowerCase() === data.name.toLowerCase());
          if (person) {
            updatePerson(person.id, { preferences: [...person.preferences, { id: crypto.randomUUID(), category: 'fact', content: data.content, timestamp: Date.now() }] });
          }
        } else if (action === 'UPDATE_PERSON' && data.name) {
          const person = people.find(p => p.name.toLowerCase() === data.name.toLowerCase());
          if (person) {
            const updates: Partial<Person> = {};
            if (data.phone) updates.phone = data.phone;
            if (data.email) updates.email = data.email;
            if (data.address) updates.address = data.address;
            if (data.relationship) updates.relationship = data.relationship;
            if (data.notes) updates.notes = data.notes;
            if (data.nickname) updates.nickname = data.nickname;
            updatePerson(person.id, updates);
          }
        }
      }
    } catch (err) {
      setChatHistory(prev => [...prev, { role: 'bot', content: "Something went wrong. Let's try again." }]);
    } finally {
      setIsProcessing(false);
    }
  };

  const toggleListening = () => {
    const SpeechRecognition = (window as any).SpeechRecognition || (window as any).webkitSpeechRecognition;
    if (!SpeechRecognition) return alert("Speech recognition not supported.");
    if (isListening) return setIsListening(false);
    const recognition = new SpeechRecognition();
    recognition.lang = 'en-US';
    recognition.onstart = () => setIsListening(true);
    recognition.onresult = (event: any) => setChatInput(event.results[0][0].transcript);
    recognition.onerror = () => setIsListening(false);
    recognition.onend = () => setIsListening(false);
    recognition.start();
  };

  const selectedPerson = people.find(p => p.id === selectedPersonId);

  return (
    <div className="min-h-screen max-w-2xl mx-auto flex flex-col relative pb-8 pt-12 px-4 font-sans text-[var(--ink)]">
      <main className="flex-1">
        {selectedPersonId ? (
          <PersonProfile 
            person={selectedPerson!} 
            onBack={() => setSelectedPersonId(null)} 
            onUpdate={(updates) => updatePerson(selectedPerson!.id, updates)}
            onDelete={deletePerson}
          />
        ) : (
          <>
            {view === 'home' && (
              <HomeView 
                people={people} chatHistory={chatHistory} chatInput={chatInput} setChatInput={setChatInput}
                isProcessing={isProcessing} isListening={isListening} toggleListening={toggleListening}
                handleAIChat={handleAIChat} setView={setView} setSelectedPersonId={setSelectedPersonId} upcomingEvents={upcomingEvents}
              />
            )}
            {view === 'rolodex' && (
              <div className="animate-in slide-in-from-bottom-4 duration-500 pb-20">
                <button onClick={() => setView('home')} className="mb-6 flex items-center gap-2 opacity-50 hover:opacity-100 group transition-all">
                  <ArrowLeft className="w-4 h-4 group-hover:translate-x-[-2px] transition-transform" /> <span>Back Home</span>
                </button>
                <div className="flex justify-between items-center mb-8">
                  <h2 className="text-3xl font-serif">Rolodex</h2>
                  <button onClick={() => setShowAddModal(true)} className="p-3 bg-[var(--ink)] text-[var(--card)] rounded-2xl shadow-lg hover:rotate-90 transition-transform"><Plus className="w-6 h-6" /></button>
                </div>
                <div className="grid gap-3">
                  {sortedPeople.length === 0 ? (
                    <div className="text-center py-20 opacity-30 italic text-sm">Circle is empty. Add someone.</div>
                  ) : sortedPeople.map(p => (
                    <div key={p.id} onClick={() => setSelectedPersonId(p.id)} className="kokoro-card p-4 rounded-2xl flex items-center gap-4 cursor-pointer hover:border-[var(--primary)] transition-all group">
                      <img src={p.avatar} className="w-12 h-12 rounded-full border border-[var(--soft)] group-hover:border-[var(--primary)]" />
                      <div>
                        <div className="flex items-center gap-2"><p className="font-medium">{p.name}</p>{p.pinned && <Star className="w-3 h-3 text-[var(--primary)] fill-[var(--primary)]" />}</div>
                        <p className="text-[10px] uppercase tracking-wider opacity-40">{p.relationship}</p>
                      </div>
                      <ChevronRight className="ml-auto w-4 h-4 opacity-20 group-hover:translate-x-1 group-hover:opacity-100 transition-all" />
                    </div>
                  ))}
                </div>
              </div>
            )}
            {view === 'settings' && (
              <div className="animate-in fade-in duration-500">
                <button onClick={() => setView('home')} className="mb-6 flex items-center gap-2 opacity-50 hover:opacity-100"><ArrowLeft className="w-4 h-4" /> <span>Back Home</span></button>
                <h2 className="text-3xl font-serif mb-8">Settings</h2>
                <label className="block text-[10px] uppercase font-bold opacity-30 mb-4 tracking-widest">Appearance</label>
                <div className="grid grid-cols-2 gap-3">
                  {THEMES.map(t => (
                    <button key={t} onClick={() => setTheme(t)} className={`p-5 rounded-2xl border-2 capitalize ${theme === t ? 'border-[var(--primary)] bg-[var(--card)] shadow-md' : 'border-transparent bg-[var(--soft)] bg-opacity-20'}`}>{t}</button>
                  ))}
                </div>
              </div>
            )}
            {view === 'manifesto' && (
              <div className="animate-in fade-in duration-500">
                <button onClick={() => setView('home')} className="mb-6 flex items-center gap-2 opacity-50 hover:opacity-100"><ArrowLeft className="w-4 h-4" /> <span>Back Home</span></button>
                <article className="prose prose-stone text-[var(--ink)]">
                  <h2 className="text-4xl font-serif mb-8 text-[var(--primary)]">Ethos</h2>
                  <div className="space-y-6 opacity-80 text-lg leading-relaxed font-light italic border-l-2 border-[var(--primary)] pl-6">
                    <p>Connection is not an algorithm. It is the cultivation of the small nuances.</p>
                    <p>Kokoro exists to help you hold those details sacred.</p>
                  </div>
                </article>
              </div>
            )}
          </>
        )}
      </main>

      {!selectedPersonId && (
        <div className="fixed bottom-8 right-6 z-40">
          <button onClick={() => setView('settings')} className="w-14 h-14 bg-[var(--card)] text-[var(--ink)] rounded-2xl flex items-center justify-center shadow-2xl border border-[var(--soft)] hover:rotate-90 transition-transform active:scale-90"><Settings className="w-6 h-6 opacity-60" /></button>
        </div>
      )}

      {showAddModal && (
        <div className="fixed inset-0 bg-black bg-opacity-40 backdrop-blur-sm flex items-center justify-center p-6 z-50">
          <div className="bg-[var(--card)] text-[var(--ink)] rounded-3xl w-full max-w-md p-8 animate-in zoom-in-95 shadow-2xl border border-[var(--soft)]">
            <h2 className="text-2xl font-serif mb-6 text-[var(--primary)]">New Companion</h2>
            <form onSubmit={(e) => {
              e.preventDefault();
              const formData = new FormData(e.currentTarget);
              addPerson(formData.get('name') as string, formData.get('rel') as string);
              setShowAddModal(false);
            }} className="space-y-4">
              <div><label className="block text-[10px] uppercase opacity-40 font-bold mb-1">Name</label><input required name="name" type="text" className="w-full px-4 py-3 rounded-xl bg-[var(--bg)] border border-[var(--soft)] outline-none focus:ring-2 focus:ring-[var(--primary)]" /></div>
              <div><label className="block text-[10px] uppercase opacity-40 font-bold mb-1">Relationship</label><input required name="rel" type="text" className="w-full px-4 py-3 rounded-xl bg-[var(--bg)] border border-[var(--soft)] outline-none focus:ring-2 focus:ring-[var(--primary)]" /></div>
              <div className="flex gap-3 pt-4"><button type="button" onClick={() => setShowAddModal(false)} className="flex-1 py-3 opacity-50">Cancel</button><button type="submit" className="flex-1 py-3 bg-[var(--ink)] text-[var(--bg)] rounded-xl font-medium shadow-lg">Save Profile</button></div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

const CalendarView: React.FC<{ people: Person[], onSelectPerson: (id: string) => void }> = ({ people, onSelectPerson }) => {
  const [viewDate, setViewDate] = useState(new Date());
  const daysInMonth = new Date(viewDate.getFullYear(), viewDate.getMonth() + 1, 0).getDate();
  const firstDay = new Date(viewDate.getFullYear(), viewDate.getMonth(), 1).getDay();
  const monthEvents = useMemo(() => {
    return people.flatMap(p => p.dates.map(d => ({ ...d, personId: p.id, dObj: new Date(d.date) }))).filter(e => e.dObj.getMonth() === viewDate.getMonth() && e.dObj.getFullYear() === viewDate.getFullYear());
  }, [people, viewDate]);

  return (
    <div className="kokoro-card p-6 rounded-2xl border-[var(--soft)] shadow-md">
      <div className="flex items-center justify-between mb-8">
        <button onClick={() => setViewDate(new Date(viewDate.setMonth(viewDate.getMonth() - 1)))} className="p-2"><ChevronLeft className="w-4 h-4"/></button>
        <h3 className="font-serif text-xl font-bold">{viewDate.toLocaleDateString(undefined, { month: 'long', year: 'numeric' })}</h3>
        <button onClick={() => setViewDate(new Date(viewDate.setMonth(viewDate.getMonth() + 1)))} className="p-2"><ChevronRight className="w-4 h-4"/></button>
      </div>
      <div className="grid grid-cols-7 gap-1">
        {['S','M','T','W','T','F','S'].map(d => <div key={d} className="text-center text-[10px] font-bold opacity-30 py-2">{d}</div>)}
        {Array.from({ length: firstDay }).map((_, i) => <div key={`empty-${i}`} className="aspect-square opacity-5 text-xs flex items-center justify-center">?</div>)}
        {Array.from({ length: daysInMonth }).map((_, i) => {
          const day = i + 1;
          const hits = monthEvents.filter(e => e.dObj.getDate() === day);
          const isToday = new Date().toDateString() === new Date(viewDate.getFullYear(), viewDate.getMonth(), day).toDateString();
          return (
            <div key={day} onClick={() => hits.length > 0 && onSelectPerson(hits[0].personId)} className={`aspect-square relative flex flex-col items-center justify-start pt-2 rounded-lg text-sm transition-all ${hits.length > 0 ? 'cursor-pointer hover:border-[var(--primary)] bg-[var(--primary)] bg-opacity-5' : ''} ${isToday ? 'border-[var(--primary)] text-[var(--primary)] font-bold' : 'opacity-70'}`}>
              <span>{day}</span>
              <div className="flex gap-0.5 mt-auto mb-1">{hits.map((_, idx) => <div key={idx} className="w-1 h-1 bg-[var(--primary)] rounded-full" />)}</div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

const PersonProfile: React.FC<{ 
  person: Person, onBack: () => void, onUpdate: (updates: Partial<Person>) => void, onDelete: (pid: string) => void
}> = ({ person, onBack, onUpdate, onDelete }) => {
  const [insights, setInsights] = useState<string | null>(null);
  const [products, setProducts] = useState<ProductRecommendation[]>([]);
  const [loadingInsights, setLoadingInsights] = useState(false);
  const [loadingProducts, setLoadingProducts] = useState(false);
  const [newPref, setNewPref] = useState('');
  const [newDateLabel, setNewDateLabel] = useState('');
  const [newDateValue, setNewDateValue] = useState('');
  const [newDateRecurring, setNewDateRecurring] = useState(true);
  const dateInputRef = useRef<HTMLInputElement>(null);

  const fetchInsights = async () => {
    setLoadingInsights(true);
    const res = await getRelationshipAdvice(person);
    setInsights(res);
    setLoadingInsights(false);
  };

  const fetchProducts = async () => {
    setLoadingProducts(true);
    const res = await getProductRecommendations(person);
    setProducts(res);
    setLoadingProducts(false);
  };

  const handleQuickOccasion = (occ: string) => {
    setNewDateLabel(occ);
    let auto = false;
    if (occ === 'Christmas') { setNewDateValue(getNextOccurrence(11, 25)); auto = true; }
    else if (occ === 'Valentine\'s Day') { setNewDateValue(getNextOccurrence(1, 14)); auto = true; }
    else setNewDateValue('');
    if (!auto && dateInputRef.current) dateInputRef.current.focus();
  };

  return (
    <div className="py-4 animate-in fade-in slide-in-from-right-4 duration-500 pb-20">
      <div className="flex justify-between items-center mb-6">
        <button onClick={onBack} className="flex items-center gap-2 opacity-50 hover:opacity-100 transition-colors group"><ArrowLeft className="w-4 h-4 group-hover:translate-x-[-2px] transition-transform" /><span className="text-sm">Back Home</span></button>
        <button onClick={() => onUpdate({ pinned: !person.pinned })} className="p-2 transition-transform active:scale-125"><Star className={`w-5 h-5 ${person.pinned ? 'text-[var(--primary)] fill-[var(--primary)]' : 'opacity-20'}`} /></button>
      </div>

      <div className="flex items-center gap-6 mb-12">
        <img src={person.avatar} className="w-24 h-24 rounded-full border-4 border-[var(--card)] shadow-xl" />
        <div><h2 className="text-4xl font-serif mb-1">{person.name}</h2><p className="opacity-50 flex items-center gap-2 text-sm"><Sparkles className="w-4 h-4 text-[var(--primary)]" />{person.relationship}</p></div>
      </div>

      <div className="space-y-12">
        <section className="kokoro-card p-6 rounded-2xl space-y-4 border-[var(--soft)] shadow-md">
          <div className="grid grid-cols-1 gap-2">
            {[ { icon: Phone, k: 'phone', l: 'Phone' }, { icon: Mail, k: 'email', l: 'Email' }, { icon: MapPin, k: 'address', l: 'Address' } ].map(item => (
              <div key={item.k} className="flex items-center gap-3 p-3 bg-[var(--bg)] rounded-xl border border-[var(--soft)] focus-within:border-[var(--primary)]"><item.icon className="w-4 h-4 opacity-40" /><input placeholder={item.l} value={(person as any)[item.k] || ''} onChange={(e) => onUpdate({ [item.k]: e.target.value })} className="bg-transparent text-sm w-full outline-none" /></div>
            ))}
          </div>
        </section>

        <section>
          <h3 className="text-xl mb-4 font-serif font-bold">Deep Nuances</h3>
          <form onSubmit={(e) => { e.preventDefault(); if (!newPref.trim()) return; onUpdate({ preferences: [...person.preferences, { id: crypto.randomUUID(), category: 'fact', content: newPref, timestamp: Date.now() }] }); setNewPref(''); }} className="mb-4 relative"><input value={newPref} onChange={(e) => setNewPref(e.target.value)} placeholder="Add likes, rituals..." className="w-full pl-4 pr-12 py-4 bg-[var(--card)] border border-[var(--soft)] rounded-xl outline-none" /><button type="submit" className="absolute right-2 top-2 bottom-2 w-10 flex items-center justify-center bg-[var(--ink)] text-[var(--bg)] rounded-lg"><Plus className="w-5 h-5"/></button></form>
          <div className="flex flex-wrap gap-2">{person.preferences.map(p => (<div key={p.id} className="px-3 py-1.5 bg-[var(--soft)] bg-opacity-20 border border-[var(--soft)] rounded-full text-xs flex items-center gap-2 group hover:border-[var(--primary)]"><Coffee className="w-3 h-3 opacity-40 group-hover:text-[var(--primary)]" />{p.content}<button onClick={() => onUpdate({ preferences: person.preferences.filter(pr => pr.id !== p.id) })} className="opacity-30 hover:opacity-100 hover:text-red-500"><Trash2 className="w-3.5 h-3.5"/></button></div>))}</div>
        </section>

        <section>
          <h3 className="text-xl mb-4 font-serif font-bold">Occasion Management</h3>
          <div className="flex flex-wrap gap-2 mb-6">
